BJECT_ID('Permissions', 'U') IS NOT NULL DROP TABLE Permissions; IF OBJECT_ID('Roles', 'U') IS NOT NULL DROP TABLE Roles; IF OBJECT_ID('Users', 'U') IS NOT NULL DROP TABLE Users; IF OBJECT_ID('XC_APPCONFIG', 'U') IS NOT NULL DROP TABLE XC_APPCONFIG;
-- ====================================== -- CORE SYSTEM TABLES -- ======================================
-- Application Configuration Table CREATE TABLE XC_APPCONFIG ( ConfigId BIGINT IDENTITY(1,1) NOT NULL, ConfigKey NVARCHAR(255) NOT NULL, ConfigValue NVARCHAR(MAX) NULL, Description NVARCHAR(500) NULL, DataType NVARCHAR(50) DEFAULT 'String' NOT NULL, IsEncrypted BIT DEFAULT 0 NOT NULL, Category NVARCHAR(100) DEFAULT 'General' NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CreatedBy BIGINT NULL, UpdatedBy BIGINT NULL, Active BIT DEFAULT 1 NOT NULL, CONSTRAINT PK_XC_APPCONFIG PRIMARY KEY CLUSTERED (ConfigId), CONSTRAINT UQ_XC_APPCONFIG_ConfigKey UNIQUE (ConfigKey) );
-- Users Table - Core user information CREATE TABLE Users ( UserId BIGINT IDENTITY(1,1) NOT NULL, FirstName NVARCHAR(100) NOT NULL, LastName NVARCHAR(100) NOT NULL, Email NVARCHAR(255) NOT NULL, Phone NVARCHAR(20) NULL, PasswordHash NVARCHAR(255) NOT NULL, Salt NVARCHAR(255) NOT NULL, TenantId BIGINT NULL, Active BIT DEFAULT 1 NOT NULL, EmailVerified BIT DEFAULT 0 NOT NULL, PhoneVerified BIT DEFAULT 0 NOT NULL, LoginAttempts INT DEFAULT 0 NOT NULL, AccountLocked BIT DEFAULT 0 NOT NULL, LastLoginAttempt DATETIME2(7) NULL, LastLogin DATETIME2(7) NULL, LastLogout DATETIME2(7) NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, -- Extended Profile Fields ProfilePicture NVARCHAR(500) NULL, DateOfBirth DATE NULL, Gender NVARCHAR(20) NULL, Timezone NVARCHAR(100) DEFAULT 'UTC' NULL, Language NVARCHAR(10) DEFAULT 'en' NULL, Country NVARCHAR(100) NULL, City NVARCHAR(100) NULL, State NVARCHAR(100) NULL, PostalCode NVARCHAR(20) NULL, Bio NVARCHAR(MAX) NULL, Website NVARCHAR(255) NULL, CompanyName NVARCHAR(255) NULL, JobTitle NVARCHAR(255) NULL, PreferredContactMethod NVARCHAR(50) DEFAULT 'Email' NULL, NotificationSettings NVARCHAR(MAX) NULL, -- JSON PrivacySettings NVARCHAR(MAX) NULL, -- JSON -- Security Fields PasswordChangedAt DATETIME2(7) NULL, LastPasswordReset DATETIME2(7) NULL, RememberMeToken NVARCHAR(255) NULL, RememberMeExpiry DATETIME2(7) NULL, AgreeToTerms BIT DEFAULT 0 NOT NULL, TermsAcceptedAt DATETIME2(7) NULL, CONSTRAINT PK_Users PRIMARY KEY CLUSTERED (UserId), CONSTRAINT UQ_Users_Email UNIQUE (Email), CONSTRAINT UQ_Users_Phone UNIQUE (Phone), CONSTRAINT CK_Users_LoginAttempts CHECK (LoginAttempts >= 0 AND LoginAttempts <= 10) );
-- Roles Table - User roles and permissions CREATE TABLE Roles ( RoleId BIGINT IDENTITY(1,1) NOT NULL, RoleName NVARCHAR(50) NOT NULL, RoleDescription NVARCHAR(255) NULL, RoleLevel INT DEFAULT 1 NOT NULL, -- Hierarchy level IsSystemRole BIT DEFAULT 0 NOT NULL, Active BIT DEFAULT 1 NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CreatedBy BIGINT NULL, UpdatedBy BIGINT NULL, CONSTRAINT PK_Roles PRIMARY KEY CLUSTERED (RoleId), CONSTRAINT UQ_Roles_RoleName UNIQUE (RoleName), CONSTRAINT CK_Roles_RoleLevel CHECK (RoleLevel >= 1 AND RoleLevel <= 10) );
-- Permissions Table - System permissions CREATE TABLE Permissions ( PermissionId BIGINT IDENTITY(1,1) NOT NULL, PermissionName NVARCHAR(100) NOT NULL, PermissionDescription NVARCHAR(255) NULL, PermissionCategory NVARCHAR(100) DEFAULT 'General' NULL, ResourceType NVARCHAR(50) NULL, -- Product, Order, User, etc. ActionType NVARCHAR(50) NULL, -- View, Create, Update, Delete Active BIT DEFAULT 1 NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_Permissions PRIMARY KEY CLUSTERED (PermissionId), CONSTRAINT UQ_Permissions_PermissionName UNIQUE (PermissionName) );
-- User Roles Junction Table CREATE TABLE UserRoles ( UserRoleId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, RoleId BIGINT NOT NULL, AssignedBy BIGINT NULL, AssignedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, ExpiresAt DATETIME2(7) NULL, Active BIT DEFAULT 1 NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_UserRoles PRIMARY KEY CLUSTERED (UserRoleId), CONSTRAINT FK_UserRoles_Users FOREIGN KEY (UserId) REFERENCES Users(UserId), CONSTRAINT FK_UserRoles_Roles FOREIGN KEY (RoleId) REFERENCES Roles(RoleId), CONSTRAINT FK_UserRoles_AssignedBy FOREIGN KEY (AssignedBy) REFERENCES Users(UserId), CONSTRAINT UQ_UserRoles_UserRole UNIQUE (UserId, RoleId, Active) );
-- Role Permissions Junction Table CREATE TABLE RolePermissions ( RolePermissionId BIGINT IDENTITY(1,1) NOT NULL, RoleId BIGINT NOT NULL, PermissionId BIGINT NOT NULL, Active BIT DEFAULT 1 NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_RolePermissions PRIMARY KEY CLUSTERED (RolePermissionId), CONSTRAINT FK_RolePermissions_Roles FOREIGN KEY (RoleId) REFERENCES Roles(RoleId), CONSTRAINT FK_RolePermissions_Permissions FOREIGN KEY (PermissionId) REFERENCES Permissions(PermissionId), CONSTRAINT UQ_RolePermissions_RolePermission UNIQUE (RoleId, PermissionId) );
-- User Custom Permissions Table CREATE TABLE UserCustomPermissions ( UserCustomPermissionId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, PermissionId BIGINT NOT NULL, GrantedBy BIGINT NOT NULL, GrantedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, ExpiresAt DATETIME2(7) NULL, Active BIT DEFAULT 1 NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_UserCustomPermissions PRIMARY KEY CLUSTERED (UserCustomPermissionId), CONSTRAINT FK_UserCustomPermissions_Users FOREIGN KEY (UserId) REFERENCES Users(UserId), CONSTRAINT FK_UserCustomPermissions_Permissions FOREIGN KEY (PermissionId) REFERENCES Permissions(PermissionId), CONSTRAINT FK_UserCustomPermissions_GrantedBy FOREIGN KEY (GrantedBy) REFERENCES Users(UserId), CONSTRAINT UQ_UserCustomPermissions_UserPermission UNIQUE (UserId, PermissionId) );
-- ====================================== -- PRODUCT CATALOG TABLES -- ======================================
-- Menu Master Table CREATE TABLE MenuMaster ( MenuId BIGINT IDENTITY(1,1) NOT NULL, MenuName NVARCHAR(255) NOT NULL, OrderBy INT DEFAULT 0 NOT NULL, Active BIT DEFAULT 1 NOT NULL, Image NVARCHAR(500) NULL, SubMenu BIT DEFAULT 0 NOT NULL, TenantId BIGINT NULL, Created DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, Modified DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CreatedBy BIGINT NULL, ModifiedBy BIGINT NULL, CONSTRAINT PK_MenuMaster PRIMARY KEY CLUSTERED (MenuId), CONSTRAINT FK_MenuMaster_CreatedBy FOREIGN KEY (CreatedBy) REFERENCES Users(UserId), CONSTRAINT FK_MenuMaster_ModifiedBy FOREIGN KEY (ModifiedBy) REFERENCES Users(UserId) );
-- Categories Table CREATE TABLE Categories ( CategoryId BIGINT IDENTITY(1,1) NOT NULL, CategoryName NVARCHAR(255) NOT NULL, Description NVARCHAR(MAX) NULL, Active BIT DEFAULT 1 NOT NULL, ParentCategoryId BIGINT NULL, OrderBy INT DEFAULT 0 NOT NULL, Icon NVARCHAR(255) NULL, HasSubMenu BIT DEFAULT 0 NOT NULL, Link NVARCHAR(500) NULL, TenantId BIGINT NULL, MenuId BIGINT NULL, Created DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, Modified DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CreatedBy BIGINT NULL, ModifiedBy BIGINT NULL, CONSTRAINT PK_Categories PRIMARY KEY CLUSTERED (CategoryId), CONSTRAINT FK_Categories_ParentCategory FOREIGN KEY (ParentCategoryId) REFERENCES Categories(CategoryId), CONSTRAINT FK_Categories_Menu FOREIGN KEY (MenuId) REFERENCES MenuMaster(MenuId), CONSTRAINT FK_Categories_CreatedBy FOREIGN KEY (CreatedBy) REFERENCES Users(UserId), CONSTRAINT FK_Categories_ModifiedBy FOREIGN KEY (ModifiedBy) REFERENCES Users(UserId), CONSTRAINT UQ_Categories_TenantName UNIQUE (TenantId, CategoryName) );
-- Product Categories Table (Alternative structure for flexibility) CREATE TABLE ProductCategories ( CategoryId BIGINT IDENTITY(1,1) NOT NULL, Category NVARCHAR(255) NOT NULL, Active BIT DEFAULT 1 NOT NULL, OrderBy INT DEFAULT 0 NOT NULL, Description NVARCHAR(MAX) NULL, Icon NVARCHAR(255) NULL, SubMenu BIT DEFAULT 0 NOT NULL, TenantId BIGINT NULL, Created DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, Modified DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_ProductCategories PRIMARY KEY CLUSTERED (CategoryId) );
-- Products Table - Core product information CREATE TABLE Products ( ProductId BIGINT IDENTITY(1,1) NOT NULL, TenantId BIGINT NULL, ProductName NVARCHAR(255) NOT NULL, ProductDescription NVARCHAR(500) NULL, ProductCode NVARCHAR(100) NOT NULL, FullDescription NVARCHAR(MAX) NULL, Specification NVARCHAR(MAX) NULL, Story NVARCHAR(MAX) NULL, PackQuantity INT DEFAULT 1 NOT NULL, Quantity INT DEFAULT 0 NOT NULL, Total INT DEFAULT 0 NOT NULL, Price DECIMAL(18,2) NOT NULL, Category BIGINT NULL, Rating DECIMAL(3,2) DEFAULT 0 NOT NULL, Active BIT DEFAULT 1 NOT NULL, Trending INT DEFAULT 0 NOT NULL, UserBuyCount INT DEFAULT 0 NOT NULL, [Return] INT DEFAULT 30 NOT NULL, -- Return policy in days InStock BIT COMPUTED (CASE WHEN Quantity > 0 THEN 1 ELSE 0 END), BestSeller BIT DEFAULT 0 NOT NULL, DeliveryDate INT DEFAULT 7 NOT NULL, -- Delivery days Offer NVARCHAR(100) NULL, OrderBy INT DEFAULT 0 NOT NULL, UserId BIGINT NULL, Overview NVARCHAR(MAX) NULL, LongDescription NVARCHAR(MAX) NULL, -- SEO and Marketing MetaTitle NVARCHAR(255) NULL, MetaDescription NVARCHAR(500) NULL, MetaKeywords NVARCHAR(500) NULL, Slug NVARCHAR(255) NULL, -- Inventory Management SKU NVARCHAR(100) NULL, Barcode NVARCHAR(100) NULL, Weight DECIMAL(10,3) NULL, Dimensions NVARCHAR(100) NULL, -- L x W x H MinStockLevel INT DEFAULT 0 NOT NULL, MaxStockLevel INT NULL, ReorderPoint INT DEFAULT 0 NOT NULL, -- Pricing CostPrice DECIMAL(18,2) NULL, OriginalPrice DECIMAL(18,2) NULL, DiscountPercentage DECIMAL(5,2) DEFAULT 0 NOT NULL, -- Timestamps Created DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, Modified DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CreatedBy BIGINT NULL, ModifiedBy BIGINT NULL, DeletedAt DATETIME2(7) NULL, DeletedBy BIGINT NULL, CONSTRAINT PK_Products PRIMARY KEY CLUSTERED (ProductId), CONSTRAINT FK_Products_Category FOREIGN KEY (Category) REFERENCES Categories(CategoryId), CONSTRAINT FK_Products_CreatedBy FOREIGN KEY (CreatedBy) REFERENCES Users(UserId), CONSTRAINT FK_Products_ModifiedBy FOREIGN KEY (ModifiedBy) REFERENCES Users(UserId), CONSTRAINT FK_Products_DeletedBy FOREIGN KEY (DeletedBy) REFERENCES Users(UserId), CONSTRAINT UQ_Products_TenantCode UNIQUE (TenantId, ProductCode), CONSTRAINT UQ_Products_SKU UNIQUE (SKU), CONSTRAINT CK_Products_Price CHECK (Price >= 0), CONSTRAINT CK_Products_Quantity CHECK (Quantity >= 0), CONSTRAINT CK_Products_Rating CHECK (Rating >= 0 AND Rating <= 5), CONSTRAINT CK_Products_DiscountPercentage CHECK (DiscountPercentage >= 0 AND DiscountPercentage <= 100) );
-- Product Images Table CREATE TABLE ProductImages ( ImageId BIGINT IDENTITY(1,1) NOT NULL, ProductId BIGINT NOT NULL, ImageName NVARCHAR(255) NOT NULL, ContentType NVARCHAR(100) NOT NULL, FileSize BIGINT NOT NULL, ImageData VARBINARY(MAX) NULL, ThumbnailData VARBINARY(MAX) NULL, Poster NVARCHAR(500) NULL, -- URL/Path Main BIT DEFAULT 0 NOT NULL, Active BIT DEFAULT 1 NOT NULL, OrderBy INT DEFAULT 0 NOT NULL, AltText NVARCHAR(255) NULL, Caption NVARCHAR(500) NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, Modified DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CreatedBy BIGINT NULL, ModifiedBy BIGINT NULL, DeletedAt DATETIME2(7) NULL, DeletedBy BIGINT NULL, CONSTRAINT PK_ProductImages PRIMARY KEY CLUSTERED (ImageId), CONSTRAINT FK_ProductImages_Products FOREIGN KEY (ProductId) REFERENCES Products(ProductId) ON DELETE CASCADE, CONSTRAINT FK_ProductImages_CreatedBy FOREIGN KEY (CreatedBy) REFERENCES Users(UserId), CONSTRAINT FK_ProductImages_ModifiedBy FOREIGN KEY (ModifiedBy) REFERENCES Users(UserId), CONSTRAINT FK_ProductImages_DeletedBy FOREIGN KEY (DeletedBy) REFERENCES Users(UserId), CONSTRAINT CK_ProductImages_FileSize CHECK (FileSize > 0 AND FileSize <= 52428800) -- Max 50MB );
-- Product Reviews Table CREATE TABLE ProductReviews ( ReviewId BIGINT IDENTITY(1,1) NOT NULL, ProductId BIGINT NOT NULL, UserId BIGINT NOT NULL, Rating INT NOT NULL, ReviewTitle NVARCHAR(255) NULL, ReviewText NVARCHAR(MAX) NULL, IsVerifiedPurchase BIT DEFAULT 0 NOT NULL, IsApproved BIT DEFAULT 0 NOT NULL, HelpfulCount INT DEFAULT 0 NOT NULL, Active BIT DEFAULT 1 NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, ApprovedAt DATETIME2(7) NULL, ApprovedBy BIGINT NULL, CONSTRAINT PK_ProductReviews PRIMARY KEY CLUSTERED (ReviewId), CONSTRAINT FK_ProductReviews_Products FOREIGN KEY (ProductId) REFERENCES Products(ProductId) ON DELETE CASCADE, CONSTRAINT FK_ProductReviews_Users FOREIGN KEY (UserId) REFERENCES Users(UserId), CONSTRAINT FK_ProductReviews_ApprovedBy FOREIGN KEY (ApprovedBy) REFERENCES Users(UserId), CONSTRAINT UQ_ProductReviews_UserProduct UNIQUE (UserId, ProductId), CONSTRAINT CK_ProductReviews_Rating CHECK (Rating >= 1 AND Rating <= 5) );
-- Product Wishlist Table CREATE TABLE ProductWishList ( WishListId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, ProductId BIGINT NOT NULL, TenantId BIGINT NULL, Priority INT DEFAULT 0 NOT NULL, Notes NVARCHAR(500) NULL, Active BIT DEFAULT 1 NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_ProductWishList PRIMARY KEY CLUSTERED (WishListId), CONSTRAINT FK_ProductWishList_Users FOREIGN KEY (UserId) REFERENCES Users(UserId), CONSTRAINT FK_ProductWishList_Products FOREIGN KEY (ProductId) REFERENCES Products(ProductId) ON DELETE CASCADE, CONSTRAINT UQ_ProductWishList_UserProduct UNIQUE (UserId, ProductId) );
-- ====================================== -- SHOPPING CART TABLES -- ======================================
-- Cart Items Table CREATE TABLE CartItems ( CartId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, ProductId BIGINT NOT NULL, Quantity INT NOT NULL, TenantId BIGINT NULL, SessionId NVARCHAR(255) NULL, Active BIT DEFAULT 1 NOT NULL, AddedDate DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedDate DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, ExpiresAt DATETIME2(7) NULL, -- Cart expiration CONSTRAINT PK_CartItems PRIMARY KEY CLUSTERED (CartId), CONSTRAINT FK_CartItems_Users FOREIGN KEY (UserId) REFERENCES Users(UserId), CONSTRAINT FK_CartItems_Products FOREIGN KEY (ProductId) REFERENCES Products(ProductId) ON DELETE CASCADE, CONSTRAINT UQ_CartItems_UserProduct UNIQUE (UserId, ProductId, Active), CONSTRAINT CK_CartItems_Quantity CHECK (Quantity > 0) );
-- ====================================== -- ORDER MANAGEMENT TABLES -- ======================================
-- Orders Table CREATE TABLE Orders ( OrderId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, TenantId BIGINT NULL, OrderNumber NVARCHAR(50) NOT NULL, OrderStatus NVARCHAR(50) DEFAULT 'Pending' NOT NULL, PaymentStatus NVARCHAR(50) DEFAULT 'Pending' NOT NULL, TotalAmount DECIMAL(18,2) NOT NULL, Subtotal DECIMAL(18,2) NOT NULL, ShippingAmount DECIMAL(18,2) DEFAULT 0 NOT NULL, TaxAmount DECIMAL(18,2) DEFAULT 0 NOT NULL, DiscountAmount DECIMAL(18,2) DEFAULT 0 NOT NULL, CurrencyCode NVARCHAR(3) DEFAULT 'USD' NOT NULL, Notes NVARCHAR(1000) NULL, -- Address Information (JSON or normalized) ShippingAddress NVARCHAR(MAX) NULL, -- JSON BillingAddress NVARCHAR(MAX) NULL, -- JSON -- Payment and Shipping Information PaymentMethod NVARCHAR(MAX) NULL, -- JSON ShippingMethod NVARCHAR(MAX) NULL, -- JSON PaymentTransactionId NVARCHAR(255) NULL, ShippingTrackingNumber NVARCHAR(255) NULL, -- Important Timestamps CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, ShippedAt DATETIME2(7) NULL, DeliveredAt DATETIME2(7) NULL, CancelledAt DATETIME2(7) NULL, -- Cancellation Information CancelReason NVARCHAR(500) NULL, CancelledBy BIGINT NULL, -- System Fields Active BIT DEFAULT 1 NOT NULL, CreatedBy BIGINT NULL, UpdatedBy BIGINT NULL, CONSTRAINT PK_Orders PRIMARY KEY CLUSTERED (OrderId), CONSTRAINT FK_Orders_Users FOREIGN KEY (UserId) REFERENCES Users(UserId), CONSTRAINT FK_Orders_CancelledBy FOREIGN KEY (CancelledBy) REFERENCES Users(UserId), CONSTRAINT FK_Orders_CreatedBy FOREIGN KEY (CreatedBy) REFERENCES Users(UserId), CONSTRAINT FK_Orders_UpdatedBy FOREIGN KEY (UpdatedBy) REFERENCES Users(UserId), CONSTRAINT UQ_Orders_OrderNumber UNIQUE (OrderNumber), CONSTRAINT CK_Orders_TotalAmount CHECK (TotalAmount >= 0), CONSTRAINT CK_Orders_Subtotal CHECK (Subtotal >= 0), CONSTRAINT CK_Orders_ShippingAmount CHECK (ShippingAmount >= 0), CONSTRAINT CK_Orders_TaxAmount CHECK (TaxAmount >= 0), CONSTRAINT CK_Orders_DiscountAmount CHECK (DiscountAmount >= 0) );
-- Order Items Table CREATE TABLE OrderItems ( OrderItemId BIGINT IDENTITY(1,1) NOT NULL, OrderId BIGINT NOT NULL, ProductId BIGINT NOT NULL, ProductName NVARCHAR(255) NOT NULL, ProductImage NVARCHAR(500) NULL, ProductCode NVARCHAR(100) NULL, Price DECIMAL(18,2) NOT NULL, OriginalPrice DECIMAL(18,2) NULL, Quantity INT NOT NULL, Total DECIMAL(18,2) NOT NULL, DiscountAmount DECIMAL(18,2) DEFAULT 0 NOT NULL, TaxAmount DECIMAL(18,2) DEFAULT 0 NOT NULL, Active BIT DEFAULT 1 NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_OrderItems PRIMARY KEY CLUSTERED (OrderItemId), CONSTRAINT FK_OrderItems_Orders FOREIGN KEY (OrderId) REFERENCES Orders(OrderId) ON DELETE CASCADE, CONSTRAINT FK_OrderItems_Products FOREIGN KEY (ProductId) REFERENCES Products(ProductId), CONSTRAINT CK_OrderItems_Price CHECK (Price >= 0), CONSTRAINT CK_OrderItems_Quantity CHECK (Quantity > 0), CONSTRAINT CK_OrderItems_Total CHECK (Total >= 0) );
-- Order Status History Table CREATE TABLE OrderStatusHistory ( StatusHistoryId BIGINT IDENTITY(1,1) NOT NULL, OrderId BIGINT NOT NULL, PreviousStatus NVARCHAR(50) NULL, NewStatus NVARCHAR(50) NOT NULL, StatusNote NVARCHAR(1000) NULL, ChangedBy BIGINT NULL, ChangedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_OrderStatusHistory PRIMARY KEY CLUSTERED (StatusHistoryId), CONSTRAINT FK_OrderStatusHistory_Orders FOREIGN KEY (OrderId) REFERENCES Orders(OrderId) ON DELETE CASCADE, CONSTRAINT FK_OrderStatusHistory_ChangedBy FOREIGN KEY (ChangedBy) REFERENCES Users(UserId) );
-- Order Tracking Table CREATE TABLE OrderTracking ( TrackingId BIGINT IDENTITY(1,1) NOT NULL, OrderId BIGINT NOT NULL, TrackingNumber NVARCHAR(100) NULL, Carrier NVARCHAR(100) NULL, TrackingStatus NVARCHAR(50) DEFAULT 'Pending' NOT NULL, EstimatedDelivery DATETIME2(7) NULL, ActualDelivery DATETIME2(7) NULL, TrackingUrl NVARCHAR(500) NULL, ShippingCost DECIMAL(18,2) NULL, Active BIT DEFAULT 1 NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_OrderTracking PRIMARY KEY CLUSTERED (TrackingId), CONSTRAINT FK_OrderTracking_Orders FOREIGN KEY (OrderId) REFERENCES Orders(OrderId) ON DELETE CASCADE, CONSTRAINT UQ_OrderTracking_OrderActive UNIQUE (OrderId, Active) );
-- Order Refunds Table CREATE TABLE OrderRefunds ( RefundId BIGINT IDENTITY(1,1) NOT NULL, OrderId BIGINT NOT NULL, RefundAmount DECIMAL(18,2) NOT NULL, RefundReason NVARCHAR(500) NULL, RefundStatus NVARCHAR(50) DEFAULT 'Pending' NOT NULL, RefundMethod NVARCHAR(100) NULL, RefundTransactionId NVARCHAR(255) NULL, RequestedBy BIGINT NOT NULL, RequestedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, ProcessedAt DATETIME2(7) NULL, ProcessedBy BIGINT NULL, Active BIT DEFAULT 1 NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_OrderRefunds PRIMARY KEY CLUSTERED (RefundId), CONSTRAINT FK_OrderRefunds_Orders FOREIGN KEY (OrderId) REFERENCES Orders(OrderId) ON DELETE CASCADE, CONSTRAINT FK_OrderRefunds_RequestedBy FOREIGN KEY (RequestedBy) REFERENCES Users(UserId), CONSTRAINT FK_OrderRefunds_ProcessedBy FOREIGN KEY (ProcessedBy) REFERENCES Users(UserId), CONSTRAINT CK_OrderRefunds_RefundAmount CHECK (RefundAmount >= 0) );
-- Order Analytics Table CREATE TABLE OrderAnalytics ( AnalyticsId BIGINT IDENTITY(1,1) NOT NULL, OrderId BIGINT NOT NULL, UserId BIGINT NOT NULL, OrderNumber NVARCHAR(50) NOT NULL, TotalAmount DECIMAL(18,2) NOT NULL, ItemCount INT NOT NULL, OrderDate DATETIME2(7) NOT NULL, TenantId BIGINT NULL, OrderSource NVARCHAR(50) DEFAULT 'Web' NULL, -- Web, Mobile, API DeviceType NVARCHAR(50) NULL, UserAgent NVARCHAR(500) NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_OrderAnalytics PRIMARY KEY CLUSTERED (AnalyticsId), CONSTRAINT FK_OrderAnalytics_Orders FOREIGN KEY (OrderId) REFERENCES Orders(OrderId) ON DELETE CASCADE, CONSTRAINT FK_OrderAnalytics_Users FOREIGN KEY (UserId) REFERENCES Users(UserId) );
-- ====================================== -- USER MANAGEMENT TABLES -- ======================================
-- User Addresses Table CREATE TABLE UserAddresses ( AddressId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, AddressType NVARCHAR(50) DEFAULT 'Home' NOT NULL, -- Home, Work, Shipping, Billing Street NVARCHAR(255) NOT NULL, City NVARCHAR(100) NOT NULL, State NVARCHAR(100) NOT NULL, PostalCode NVARCHAR(20) NOT NULL, Country NVARCHAR(100) NOT NULL, IsDefault BIT DEFAULT 0 NOT NULL, Active BIT DEFAULT 1 NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_UserAddresses PRIMARY KEY CLUSTERED (AddressId), CONSTRAINT FK_UserAddresses_Users FOREIGN KEY (UserId) REFERENCES Users(UserId) ON DELETE CASCADE );
-- User Preferences Table CREATE TABLE UserPreferences ( PreferenceId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, PreferenceKey NVARCHAR(100) NOT NULL, PreferenceValue NVARCHAR(MAX) NULL, PreferenceType NVARCHAR(50) DEFAULT 'String' NOT NULL, -- String, Number, Boolean, JSON Category NVARCHAR(100) DEFAULT 'General' NULL, Active BIT DEFAULT 1 NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_UserPreferences PRIMARY KEY CLUSTERED (PreferenceId), CONSTRAINT FK_UserPreferences_Users FOREIGN KEY (UserId) REFERENCES Users(UserId) ON DELETE CASCADE, CONSTRAINT UQ_UserPreferences_UserKey UNIQUE (UserId, PreferenceKey) );
-- User Tokens Table CREATE TABLE UserTokens ( TokenId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, Token NVARCHAR(MAX) NOT NULL, RefreshToken NVARCHAR(MAX) NULL, TokenType NVARCHAR(50) DEFAULT 'JWT' NOT NULL, DeviceId NVARCHAR(255) NULL, DeviceInfo NVARCHAR(500) NULL, IpAddress NVARCHAR(45) NULL, UserAgent NVARCHAR(500) NULL, ExpiresAt DATETIME2(7) NOT NULL, IsRevoked BIT DEFAULT 0 NOT NULL, RevokedAt DATETIME2(7) NULL, RevokedReason NVARCHAR(255) NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_UserTokens PRIMARY KEY CLUSTERED (TokenId), CONSTRAINT FK_UserTokens_Users FOREIGN KEY (UserId) REFERENCES Users(UserId) ON DELETE CASCADE );
-- User Sessions Table CREATE TABLE UserSessions ( SessionId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, SessionToken NVARCHAR(255) NOT NULL, DeviceId NVARCHAR(255) NULL, IpAddress NVARCHAR(45) NULL, UserAgent NVARCHAR(500) NULL, LoginAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, LastActivityAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, LoggedOutAt DATETIME2(7) NULL, ExpiresAt DATETIME2(7) NOT NULL, IsActive BIT DEFAULT 1 NOT NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_UserSessions PRIMARY KEY CLUSTERED (SessionId), CONSTRAINT FK_UserSessions_Users FOREIGN KEY (UserId) REFERENCES Users(UserId) ON DELETE CASCADE, CONSTRAINT UQ_UserSessions_SessionToken UNIQUE (SessionToken) );
-- Password Reset Tokens Table CREATE TABLE PasswordResetTokens ( ResetTokenId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, ResetToken NVARCHAR(255) NOT NULL, ExpiresAt DATETIME2(7) NOT NULL, IsUsed BIT DEFAULT 0 NOT NULL, UsedAt DATETIME2(7) NULL, IpAddress NVARCHAR(45) NULL, UserAgent NVARCHAR(500) NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_PasswordResetTokens PRIMARY KEY CLUSTERED (ResetTokenId), CONSTRAINT FK_PasswordResetTokens_Users FOREIGN KEY (UserId) REFERENCES Users(UserId) ON DELETE CASCADE, CONSTRAINT UQ_PasswordResetTokens_Token UNIQUE (ResetToken) );
-- User Activity Log Table CREATE TABLE UserActivityLog ( ActivityLogId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, ActivityType NVARCHAR(100) NOT NULL, ActivityDescription NVARCHAR(MAX) NULL, IpAddress NVARCHAR(45) NULL, UserAgent NVARCHAR(500) NULL, DeviceId NVARCHAR(255) NULL, ResourceType NVARCHAR(50) NULL, -- Product, Order, User, etc. ResourceId BIGINT NULL, SessionId NVARCHAR(255) NULL, PerformedBy BIGINT NULL, -- For admin actions CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_UserActivityLog PRIMARY KEY CLUSTERED (ActivityLogId), CONSTRAINT FK_UserActivityLog_Users FOREIGN KEY (UserId) REFERENCES Users(UserId) ON DELETE CASCADE, CONSTRAINT FK_UserActivityLog_PerformedBy FOREIGN KEY (PerformedBy) REFERENCES Users(UserId) );
-- User Notifications Table CREATE TABLE UserNotifications ( NotificationId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, NotificationType NVARCHAR(50) NOT NULL, Title NVARCHAR(255) NOT NULL, Message NVARCHAR(MAX) NOT NULL, IsRead BIT DEFAULT 0 NOT NULL, ReadAt DATETIME2(7) NULL, Priority NVARCHAR(20) DEFAULT 'Normal' NOT NULL, -- Low, Normal, High, Critical ExpiresAt DATETIME2(7) NULL, ActionUrl NVARCHAR(500) NULL, ActionText NVARCHAR(100) NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_UserNotifications PRIMARY KEY CLUSTERED (NotificationId), CONSTRAINT FK_UserNotifications_Users FOREIGN KEY (UserId) REFERENCES Users(UserId) ON DELETE CASCADE );
-- Notification Queue Table CREATE TABLE NotificationQueue ( QueueId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, NotificationType NVARCHAR(50) NOT NULL, Subject NVARCHAR(255) NOT NULL, Message NVARCHAR(MAX) NOT NULL, OrderId BIGINT NULL, ProductId BIGINT NULL, Priority NVARCHAR(20) DEFAULT 'Normal' NOT NULL, Status NVARCHAR(50) DEFAULT 'Pending' NOT NULL, -- Pending, Sent, Failed ScheduledAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, SentAt DATETIME2(7) NULL, AttemptCount INT DEFAULT 0 NOT NULL, LastAttemptAt DATETIME2(7) NULL, ErrorMessage NVARCHAR(MAX) NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, UpdatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_NotificationQueue PRIMARY KEY CLUSTERED (QueueId), CONSTRAINT FK_NotificationQueue_Users FOREIGN KEY (UserId) REFERENCES Users(UserId) ON DELETE CASCADE, CONSTRAINT FK_NotificationQueue_Orders FOREIGN KEY (OrderId) REFERENCES Orders(OrderId), CONSTRAINT FK_NotificationQueue_Products FOREIGN KEY (ProductId) REFERENCES Products(ProductId) );
-- User Behavior Analytics Table CREATE TABLE UserBehaviorAnalytics ( BehaviorId BIGINT IDENTITY(1,1) NOT NULL, UserId BIGINT NOT NULL, ActionType NVARCHAR(100) NOT NULL, ActionDetails NVARCHAR(MAX) NULL, ItemCount INT NULL, TotalValue DECIMAL(18,2) NULL, SessionId NVARCHAR(255) NULL, IpAddress NVARCHAR(45) NULL, UserAgent NVARCHAR(500) NULL, DeviceType NVARCHAR(50) NULL, ReferrerUrl NVARCHAR(500) NULL, CreatedAt DATETIME2(7) DEFAULT GETUTCDATE() NOT NULL, CONSTRAINT PK_UserBehaviorAnalytics PRIMARY KEY CLUSTERED (BehaviorId), CONSTRAINT FK_UserBehaviorAnalytics_Users FOREIGN KEY (UserId) REFERENCES Users(UserId) ON DELETE CASCADE );


