-- ====================================== 
-- DATABASE MAINTENANCE PROCEDURES
-- Performance and housekeeping operations
-- ====================================== 

-- Procedure to clean up expired data
CREATE OR ALTER PROCEDURE [dbo].[SP_CLEANUP_EXPIRED_DATA]
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		DECLARE @CleanupDate DATETIME2(7) = GETUTCDATE();
		DECLARE @DeletedCount INT = 0;

		-- Clean up expired password reset tokens (older than 24 hours)
		DELETE FROM PasswordResetTokens 
		WHERE ExpiresAt < @CleanupDate OR (IsUsed = 1 AND UsedAt < DATEADD(DAY, -7, @CleanupDate));
		SET @DeletedCount = @DeletedCount + @@ROWCOUNT;

		-- Clean up expired user sessions (older than 30 days)
		DELETE FROM UserSessions 
		WHERE ExpiresAt < @CleanupDate OR (IsActive = 0 AND LoggedOutAt < DATEADD(DAY, -30, @CleanupDate));
		SET @DeletedCount = @DeletedCount + @@ROWCOUNT;

		-- Clean up old user tokens (expired and revoked tokens older than 30 days)
		DELETE FROM UserTokens 
		WHERE (ExpiresAt < @CleanupDate OR IsRevoked = 1) AND CreatedAt < DATEADD(DAY, -30, @CleanupDate);
		SET @DeletedCount = @DeletedCount + @@ROWCOUNT;

		-- Clean up old cart items (inactive items older than 90 days)
		DELETE FROM CartItems 
		WHERE Active = 0 AND UpdatedDate < DATEADD(DAY, -90, @CleanupDate);
		SET @DeletedCount = @DeletedCount + @@ROWCOUNT;

		-- Clean up expired cart items (based on ExpiresAt)
		UPDATE CartItems 
		SET Active = 0, UpdatedDate = @CleanupDate
		WHERE ExpiresAt < @CleanupDate AND Active = 1;
		SET @DeletedCount = @DeletedCount + @@ROWCOUNT;

		-- Clean up old user activity logs (older than 1 year)
		DELETE FROM UserActivityLog 
		WHERE CreatedAt < DATEADD(YEAR, -1, @CleanupDate);
		SET @DeletedCount = @DeletedCount + @@ROWCOUNT;

		-- Clean up processed notifications (older than 30 days)
		DELETE FROM NotificationQueue 
		WHERE Status = 'Sent' AND SentAt < DATEADD(DAY, -30, @CleanupDate);
		SET @DeletedCount = @DeletedCount + @@ROWCOUNT;

		-- Clean up old user behavior analytics (older than 2 years)
		DELETE FROM UserBehaviorAnalytics 
		WHERE CreatedAt < DATEADD(YEAR, -2, @CleanupDate);
		SET @DeletedCount = @DeletedCount + @@ROWCOUNT;

		PRINT 'Cleanup completed. Total records cleaned: ' + CAST(@DeletedCount AS VARCHAR(10));

	END TRY
	BEGIN CATCH
		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		PRINT 'Cleanup failed: ' + @ErrorMessage;
		THROW;
	END CATCH
END;

-- Procedure to update product statistics
CREATE OR ALTER PROCEDURE [dbo].[SP_UPDATE_PRODUCT_STATISTICS]
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		-- Update product ratings based on reviews
		UPDATE p
		SET Rating = ISNULL(avgRating.AvgRating, 0),
			Modified = GETUTCDATE()
		FROM Products p
		LEFT JOIN (
			SELECT 
				ProductId,
				AVG(CAST(Rating AS DECIMAL(3,2))) AS AvgRating
			FROM ProductReviews 
			WHERE Active = 1 AND IsApproved = 1
			GROUP BY ProductId
		) avgRating ON p.ProductId = avgRating.ProductId
		WHERE p.Active = 1;

		-- Update best seller flags based on sales
		UPDATE Products
		SET BestSeller = CASE 
			WHEN UserBuyCount >= (
				SELECT AVG(UserBuyCount) * 1.5 
				FROM Products 
				WHERE Active = 1 AND TenantId = Products.TenantId
			) THEN 1 
			ELSE 0 
		END,
		Modified = GETUTCDATE()
		WHERE Active = 1;

		-- Update trending products (based on recent sales)
		UPDATE p
		SET Trending = CASE 
			WHEN recentSales.RecentCount >= 5 THEN 1 
			ELSE 0 
		END,
		Modified = GETUTCDATE()
		FROM Products p
		LEFT JOIN (
			SELECT 
				oi.ProductId,
				COUNT(*) AS RecentCount
			FROM OrderItems oi
			INNER JOIN Orders o ON oi.OrderId = o.OrderId
			WHERE o.CreatedAt >= DATEADD(DAY, -7, GETUTCDATE())
				AND o.Active = 1 AND oi.Active = 1
			GROUP BY oi.ProductId
		) recentSales ON p.ProductId = recentSales.ProductId
		WHERE p.Active = 1;

		PRINT 'Product statistics updated successfully.';

	END TRY
	BEGIN CATCH
		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		PRINT 'Product statistics update failed: ' + @ErrorMessage;
		THROW;
	END CATCH
END;

-- Procedure to rebuild indexes for performance
CREATE OR ALTER PROCEDURE [dbo].[SP_REBUILD_INDEXES]
	@TableName NVARCHAR(255) = NULL
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @SQL NVARCHAR(MAX);
	DECLARE @IndexName NVARCHAR(255);
	DECLARE @SchemaName NVARCHAR(255) = 'dbo';

	DECLARE index_cursor CURSOR FOR
	SELECT 
		t.name AS TableName,
		i.name AS IndexName
	FROM sys.tables t
	INNER JOIN sys.indexes i ON t.object_id = i.object_id
	WHERE i.type > 0 -- Exclude heaps
		AND (@TableName IS NULL OR t.name = @TableName)
		AND t.name IN (
			'Users', 'Products', 'Orders', 'OrderItems', 'CartItems', 
			'ProductImages', 'Categories', 'UserActivityLog', 'OrderAnalytics'
		);

	OPEN index_cursor;
	FETCH NEXT FROM index_cursor INTO @TableName, @IndexName;

	WHILE @@FETCH_STATUS = 0
	BEGIN
		SET @SQL = 'ALTER INDEX ' + QUOTENAME(@IndexName) + ' ON ' + QUOTENAME(@SchemaName) + '.' + QUOTENAME(@TableName) + ' REBUILD WITH (ONLINE = OFF, SORT_IN_TEMPDB = ON);';

		BEGIN TRY
			EXEC sp_executesql @SQL;
			PRINT 'Rebuilt index: ' + @IndexName + ' on table: ' + @TableName;
		END TRY
		BEGIN CATCH
			PRINT 'Failed to rebuild index: ' + @IndexName + ' on table: ' + @TableName + ' - ' + ERROR_MESSAGE();
		END CATCH

		FETCH NEXT FROM index_cursor INTO @TableName, @IndexName;
	END

	CLOSE index_cursor;
	DEALLOCATE index_cursor;

	PRINT 'Index rebuild completed.';
END;

-- ====================================== -- PERFORMANCE MONITORING VIEWS -- Business intelligence and reporting views -- ======================================
-- Product Performance View CREATE OR ALTER VIEW [dbo].[VW_PRODUCT_PERFORMANCE] AS SELECT p.ProductId, p.TenantId, p.ProductName, p.ProductCode, p.Price, p.PriceWithDiscount, p.Quantity AS StockLevel, p.Rating, p.UserBuyCount AS TotalSales, p.BestSeller, p.Trending, p.Active, c.CategoryName, -- Performance metrics ISNULL(recentSales.Last30Days, 0) AS SalesLast30Days, ISNULL(revenue.TotalRevenue, 0) AS TotalRevenue, ISNULL(reviews.ReviewCount, 0) AS ReviewCount, ISNULL(reviews.AvgRating, 0) AS AverageRating, ISNULL(cart.InCartCount, 0) AS CurrentlyInCarts, ISNULL(wishlist.WishlistCount, 0) AS InWishlists, -- Inventory alerts CASE WHEN p.Quantity <= p.MinStockLevel THEN 'Low Stock' WHEN p.Quantity = 0 THEN 'Out of Stock' WHEN p.Quantity <= p.ReorderPoint THEN 'Reorder Soon' ELSE 'In Stock' END AS StockStatus, p.Created, p.Modified FROM Products p LEFT JOIN Categories c ON p.Category = c.CategoryId LEFT JOIN ( SELECT oi.ProductId, COUNT(*) AS Last30Days FROM OrderItems oi INNER JOIN Orders o ON oi.OrderId = o.OrderId WHERE o.CreatedAt >= DATEADD(DAY, -30, GETUTCDATE()) AND o.Active = 1 AND oi.Active = 1 GROUP BY oi.ProductId ) recentSales ON p.ProductId = recentSales.ProductId LEFT JOIN ( SELECT oi.ProductId, SUM(oi.Total) AS TotalRevenue FROM OrderItems oi INNER JOIN Orders o ON oi.OrderId = o.OrderId WHERE o.Active = 1 AND oi.Active = 1 GROUP BY oi.ProductId ) revenue ON p.ProductId = revenue.ProductId LEFT JOIN ( SELECT ProductId, COUNT(*) AS ReviewCount, AVG(CAST(Rating AS DECIMAL(3,2))) AS AvgRating FROM ProductReviews WHERE Active = 1 AND IsApproved = 1 GROUP BY ProductId ) reviews ON p.ProductId = reviews.ProductId LEFT JOIN ( SELECT ProductId, COUNT(*) AS InCartCount FROM CartItems WHERE Active = 1 GROUP BY ProductId ) cart ON p.ProductId = cart.ProductId LEFT JOIN ( SELECT ProductId, COUNT(*) AS WishlistCount FROM ProductWishList WHERE Active = 1 GROUP BY ProductId ) wishlist ON p.ProductId = wishlist.ProductId WHERE p.Active = 1;
-- Order Analytics View CREATE OR ALTER VIEW [dbo].[VW_ORDER_ANALYTICS] AS SELECT o.OrderId, o.OrderNumber, o.UserId, u.FullName AS CustomerName, u.Email AS CustomerEmail, o.TenantId, o.OrderStatus, o.PaymentStatus, o.TotalAmount, o.CreatedAt, o.UpdatedAt, -- Order metrics ISNULL(items.ItemCount, 0) AS ItemCount, ISNULL(items.TotalQuantity, 0) AS TotalQuantity, -- Time metrics DATEDIFF(HOUR, o.CreatedAt, ISNULL(o.ShippedAt, GETUTCDATE())) AS HoursToShip, DATEDIFF(HOUR, o.ShippedAt, ISNULL(o.DeliveredAt, GETUTCDATE())) AS HoursToDeliver, o.OrderAge AS DaysOld, -- Status flags CASE WHEN o.OrderStatus = 'Delivered' THEN 1 ELSE 0 END AS IsDelivered, CASE WHEN o.OrderStatus = 'Cancelled' THEN 1 ELSE 0 END AS IsCancelled, CASE WHEN o.PaymentStatus = 'Paid' THEN 1 ELSE 0 END AS IsPaid, -- Customer metrics customer.TotalOrders, customer.TotalSpent, customer.AvgOrderValue FROM Orders o INNER JOIN Users u ON o.UserId = u.UserId LEFT JOIN ( SELECT OrderId, COUNT(*) AS ItemCount, SUM(Quantity) AS TotalQuantity FROM OrderItems WHERE Active = 1 GROUP BY OrderId ) items ON o.OrderId = items.OrderId LEFT JOIN ( SELECT UserId, COUNT(*) AS TotalOrders, SUM(TotalAmount) AS TotalSpent, AVG(TotalAmount) AS AvgOrderValue FROM Orders WHERE Active = 1 GROUP BY UserId ) customer ON o.UserId = customer.UserId WHERE o.Active = 1;
-- User Activity Summary View CREATE OR ALTER VIEW [dbo].[VW_USER_ACTIVITY_SUMMARY] AS SELECT u.UserId, u.FullName, u.Email, u.TenantId, u.CreatedAt AS RegistrationDate, u.LastLogin, u.Active, u.AccountLocked, -- Activity metrics ISNULL(activity.LoginCount, 0) AS LoginCount, ISNULL(activity.LastActivityDate, u.CreatedAt) AS LastActivityDate, ISNULL(orders.OrderCount, 0) AS TotalOrders, ISNULL(orders.TotalSpent, 0) AS TotalSpent, ISNULL(cart.CartItems, 0) AS CurrentCartItems, ISNULL(wishlist.WishlistItems, 0) AS WishlistItems, -- Engagement score (0-100) CASE WHEN u.Active = 0 THEN 0 WHEN u.LastLogin IS NULL THEN 10 WHEN DATEDIFF(DAY, u.LastLogin, GETUTCDATE()) > 90 THEN 20 WHEN DATEDIFF(DAY, u.LastLogin, GETUTCDATE()) > 30 THEN 40 WHEN DATEDIFF(DAY, u.LastLogin, GETUTCDATE()) > 7 THEN 60 WHEN ISNULL(orders.OrderCount, 0) > 5 THEN 100 WHEN ISNULL(orders.OrderCount, 0) > 0 THEN 80 ELSE 50 END AS EngagementScore FROM Users u LEFT JOIN ( SELECT UserId, COUNT(*) AS LoginCount, MAX(CreatedAt) AS LastActivityDate FROM UserActivityLog WHERE ActivityType = 'LOGIN' GROUP BY UserId ) activity ON u.UserId = activity.UserId LEFT JOIN ( SELECT UserId, COUNT(*) AS OrderCount, SUM(TotalAmount) AS TotalSpent FROM Orders WHERE Active = 1 GROUP BY UserId ) orders ON u.UserId = orders.UserId LEFT JOIN ( SELECT UserId, COUNT(*) AS CartItems FROM CartItems WHERE Active = 1 GROUP BY UserId ) cart ON u.UserId = cart.UserId LEFT JOIN ( SELECT UserId, COUNT(*) AS WishlistItems FROM ProductWishList WHERE Active = 1 GROUP BY UserId ) wishlist ON u.UserId = wishlist.UserId;


-- ====================================== 
-- BUSINESS INTELLIGENCE FUNCTIONS
-- Advanced analytics and reporting functions
-- ====================================== 

-- Function to calculate customer lifetime value
CREATE OR ALTER FUNCTION [dbo].[FN_CALCULATE_CUSTOMER_LTV](@UserId BIGINT)
RETURNS DECIMAL(18,2)
AS
BEGIN
	DECLARE @LTV DECIMAL(18,2) = 0;
	DECLARE @AvgOrderValue DECIMAL(18,2) = 0;
	DECLARE @OrderFrequency DECIMAL(18,2) = 0;
	DECLARE @CustomerLifespan DECIMAL(18,2) = 0;
	DECLARE @TotalOrders INT = 0;
	DECLARE @FirstOrderDate DATETIME2(7);
	DECLARE @LastOrderDate DATETIME2(7);

	-- Get customer order statistics
	SELECT 
		@TotalOrders = COUNT(*),
		@AvgOrderValue = AVG(TotalAmount),
		@FirstOrderDate = MIN(CreatedAt),
		@LastOrderDate = MAX(CreatedAt)
	FROM Orders 
	WHERE UserId = @UserId AND Active = 1;

	IF @TotalOrders > 1
	BEGIN
		-- Calculate customer lifespan in years
		SET @CustomerLifespan = DATEDIFF(DAY, @FirstOrderDate, @LastOrderDate) / 365.0;
		IF @CustomerLifespan = 0 SET @CustomerLifespan = 1; -- Minimum 1 year

		-- Calculate order frequency (orders per year)
		SET @OrderFrequency = @TotalOrders / @CustomerLifespan;

		-- Calculate LTV: Average Order Value  Order Frequency  Customer Lifespan
		SET @LTV = @AvgOrderValue * @OrderFrequency * @CustomerLifespan;
	END
	ELSE IF @TotalOrders = 1
	BEGIN
		-- For single order customers, use average order value
		SET @LTV = @AvgOrderValue;
	END

	RETURN ISNULL(@LTV, 0);
END;

-- Function to get product recommendation score
CREATE OR ALTER FUNCTION [dbo].[FN_GET_RECOMMENDATION_SCORE](@ProductId BIGINT, @UserId BIGINT)
RETURNS DECIMAL(5,2)
AS
BEGIN
	DECLARE @Score DECIMAL(5,2) = 0;
	DECLARE @CategoryMatch DECIMAL(5,2) = 0;
	DECLARE @PriceMatch DECIMAL(5,2) = 0;
	DECLARE @RatingBonus DECIMAL(5,2) = 0;
	DECLARE @PopularityBonus DECIMAL(5,2) = 0;

	-- Category preference score (based on user's order history)
	IF EXISTS (
		SELECT 1 
		FROM Orders o
		INNER JOIN OrderItems oi ON o.OrderId = oi.OrderId
		INNER JOIN Products p1 ON oi.ProductId = p1.ProductId
		INNER JOIN Products p2 ON p2.ProductId = @ProductId
		WHERE o.UserId = @UserId 
			AND p1.Category = p2.Category 
			AND o.Active = 1 AND oi.Active = 1
	)
	BEGIN
		SET @CategoryMatch = 3.0;
	END

	-- Price preference score (within user's typical price range)
	DECLARE @AvgUserPrice DECIMAL(18,2);
	SELECT @AvgUserPrice = AVG(oi.Price)
	FROM Orders o
	INNER JOIN OrderItems oi ON o.OrderId = oi.OrderId
	WHERE o.UserId = @UserId AND o.Active = 1 AND oi.Active = 1;

	IF @AvgUserPrice IS NOT NULL
	BEGIN
		DECLARE @ProductPrice DECIMAL(18,2);
		SELECT @ProductPrice = Price FROM Products WHERE ProductId = @ProductId;

		IF ABS(@ProductPrice - @AvgUserPrice) / @AvgUserPrice <= 0.5 -- Within 50% of average
		BEGIN
			SET @PriceMatch = 2.0;
		END
	END

	-- Rating bonus
	DECLARE @ProductRating DECIMAL(3,2);
	SELECT @ProductRating = Rating FROM Products WHERE ProductId = @ProductId;
	SET @RatingBonus = ISNULL(@ProductRating, 0);

	-- Popularity bonus
	DECLARE @UserBuyCount INT;
	SELECT @UserBuyCount = UserBuyCount FROM Products WHERE ProductId = @ProductId;
	SET @PopularityBonus = CASE 
		WHEN @UserBuyCount > 100 THEN 2.0
		WHEN @UserBuyCount > 50 THEN 1.5
		WHEN @UserBuyCount > 10 THEN 1.0
		ELSE 0.5
	END;

	-- Calculate total score
	SET @Score = @CategoryMatch + @PriceMatch + @RatingBonus + @PopularityBonus;

	RETURN @Score;
END;


CREATE PROCEDURE [dbo].[SP_SEARCH_PRODUCTS]
	@TenantId BIGINT,
	@Page INT = 1,
	@Limit INT = 10,
	@Offset INT = 0,
	@Search NVARCHAR(255) = '',
	@Category INT = NULL,
	@MinPrice DECIMAL(18,2) = NULL,
	@MaxPrice DECIMAL(18,2) = NULL,
	@Rating INT = NULL,
	@InStock BIT = NULL,
	@BestSeller BIT = NULL,
	@HasOffer BIT = NULL,
	@SortBy NVARCHAR(50) = 'created',
	@SortOrder NVARCHAR(10) = 'desc'
AS
BEGIN
	SET NOCOUNT ON;

	-- Declare variables
	DECLARE @SQL NVARCHAR(MAX);
	DECLARE @WhereClause NVARCHAR(MAX) = '';
	DECLARE @OrderByClause NVARCHAR(MAX) = '';
	DECLARE @TotalCount INT = 0;

	-- Build WHERE clause dynamically
	SET @WhereClause = 'WHERE p.TenantId = ' + CAST(@TenantId AS NVARCHAR) + ' ';

	-- Search filter
	IF @Search IS NOT NULL AND @Search != ''
	BEGIN
		SET @WhereClause = @WhereClause + 
			'AND (p.ProductName LIKE ''%' + @Search + '%'' 
			 OR p.ProductDescription LIKE ''%' + @Search + '%'' 
			 OR p.ProductCode LIKE ''%' + @Search + '%'') ';
	END

	-- Category filter
	IF @Category IS NOT NULL
	BEGIN
		SET @WhereClause = @WhereClause + 'AND p.Category = ' + CAST(@Category AS NVARCHAR) + ' ';
	END

	-- Price range filter
	IF @MinPrice IS NOT NULL
	BEGIN
		SET @WhereClause = @WhereClause + 'AND p.Price >= ' + CAST(@MinPrice AS NVARCHAR) + ' ';
	END

	IF @MaxPrice IS NOT NULL
	BEGIN
		SET @WhereClause = @WhereClause + 'AND p.Price <= ' + CAST(@MaxPrice AS NVARCHAR) + ' ';
	END

	-- Rating filter
	IF @Rating IS NOT NULL
	BEGIN
		SET @WhereClause = @WhereClause + 'AND p.Rating >= ' + CAST(@Rating AS NVARCHAR) + ' ';
	END

	-- Stock filter
	IF @InStock IS NOT NULL
	BEGIN
		IF @InStock = 1
			SET @WhereClause = @WhereClause + 'AND p.Quantity > 0 ';
		ELSE
			SET @WhereClause = @WhereClause + 'AND p.Quantity = 0 ';
	END

	-- Best seller filter
	IF @BestSeller IS NOT NULL AND @BestSeller = 1
	BEGIN
		SET @WhereClause = @WhereClause + 'AND p.BestSeller = 1 ';
	END

	-- Has offer filter
	IF @HasOffer IS NOT NULL AND @HasOffer = 1
	BEGIN
		SET @WhereClause = @WhereClause + 'AND (p.Offer IS NOT NULL AND p.Offer != '''') ';
	END

	-- Active products only
	SET @WhereClause = @WhereClause + 'AND p.Active = 1 ';

	-- Build ORDER BY clause
	SET @OrderByClause = 'ORDER BY ';

	IF @SortBy = 'productName'
		SET @OrderByClause = @OrderByClause + 'p.ProductName ';
	ELSE IF @SortBy = 'price'
		SET @OrderByClause = @OrderByClause + 'p.Price ';
	ELSE IF @SortBy = 'rating'
		SET @OrderByClause = @OrderByClause + 'p.Rating ';
	ELSE IF @SortBy = 'userBuyCount'
		SET @OrderByClause = @OrderByClause + 'p.UserBuyCount ';
	ELSE
		SET @OrderByClause = @OrderByClause + 'p.Created ';

	IF @SortOrder = 'asc'
		SET @OrderByClause = @OrderByClause + 'ASC ';
	ELSE
		SET @OrderByClause = @OrderByClause + 'DESC ';

	-- Get total count first
	SET @SQL = '
	SELECT COUNT(*) as TotalCount
	FROM Products p 
	' + @WhereClause;

	-- Create temp table for count
	CREATE TABLE #TempCount (TotalCount INT);
	INSERT INTO #TempCount
	EXEC sp_executesql @SQL;

	SELECT @TotalCount = TotalCount FROM #TempCount;
	DROP TABLE #TempCount;

	-- Get paginated results
	SET @SQL = '
	SELECT 
		p.ProductId,
		p.TenantId,
		p.ProductName,
		p.ProductDescription,
		p.ProductCode,
		p.FullDescription,
		p.Specification,
		p.Story,
		p.PackQuantity,
		p.Quantity,
		p.Total,
		p.Price,
		p.Category,
		p.Rating,
		p.Active,
		p.Trending,
		p.UserBuyCount,
		p.Return,
		p.Created,
		p.Modified,
		CASE WHEN p.Quantity > 0 THEN 1 ELSE 0 END as InStock,
		p.BestSeller,
		p.DeliveryDate,
		p.Offer,
		p.OrderBy,
		p.UserId,
		p.Overview,
		p.LongDescription
	FROM Products p 
	' + @WhereClause + @OrderByClause + '
	OFFSET ' + CAST(@Offset AS NVARCHAR) + ' ROWS
	FETCH NEXT ' + CAST(@Limit AS NVARCHAR) + ' ROWS ONLY';

	-- Execute main query
	EXEC sp_executesql @SQL;

	-- Return total count as second result set
	SELECT @TotalCount as TotalCount;

END


CREATE PROCEDURE [dbo].[SP_ADD_PRODUCT]
	@TenantId BIGINT,
	@ProductName NVARCHAR(255),
	@ProductDescription NVARCHAR(500),
	@ProductCode NVARCHAR(100),
	@FullDescription NVARCHAR(MAX),
	@Specification NVARCHAR(MAX),
	@Story NVARCHAR(MAX),
	@PackQuantity INT,
	@Quantity INT,
	@Total INT,
	@Price DECIMAL(18,2),
	@Category INT,
	@Rating INT,
	@Active BIT,
	@Trending INT,
	@UserBuyCount INT,
	@Return INT,
	@BestSeller BIT,
	@DeliveryDate INT,
	@Offer NVARCHAR(100),
	@OrderBy INT,
	@UserId BIGINT,
	@CreatedBy BIGINT
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		-- Check if product code already exists for this tenant
		IF EXISTS (SELECT 1 FROM Products WHERE TenantId = @TenantId AND ProductCode = @ProductCode)
		BEGIN
			RAISERROR('Product code already exists for this tenant.', 16, 1);
			RETURN;
		END

		-- Insert new product
		INSERT INTO Products (
			TenantId,
			ProductName,
			ProductDescription,
			ProductCode,
			FullDescription,
			Specification,
			Story,
			PackQuantity,
			Quantity,
			Total,
			Price,
			Category,
			Rating,
			Active,
			Trending,
			UserBuyCount,
			[Return],
			BestSeller,
			DeliveryDate,
			Offer,
			OrderBy,
			UserId,
			Created,
			Modified,
			CreatedBy,
			ModifiedBy
		)
		VALUES (
			@TenantId,
			@ProductName,
			@ProductDescription,
			@ProductCode,
			@FullDescription,
			@Specification,
			@Story,
			@PackQuantity,
			@Quantity,
			@Total,
			@Price,
			@Category,
			@Rating,
			@Active,
			@Trending,
			@UserBuyCount,
			@Return,
			@BestSeller,
			@DeliveryDate,
			@Offer,
			@OrderBy,
			@UserId,
			GETUTCDATE(),
			GETUTCDATE(),
			@CreatedBy,
			@CreatedBy
		);

		-- Get the new product ID
		DECLARE @ProductId BIGINT = SCOPE_IDENTITY();

		-- Return the new product ID
		SELECT @ProductId AS ProductId;

		COMMIT TRANSACTION;
	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END


CREATE PROCEDURE [dbo].[SP_UPDATE_PRODUCT]
	@ProductId BIGINT,
	@TenantId BIGINT,
	@ProductName NVARCHAR(255),
	@ProductDescription NVARCHAR(500),
	@ProductCode NVARCHAR(100),
	@FullDescription NVARCHAR(MAX),
	@Specification NVARCHAR(MAX),
	@Story NVARCHAR(MAX),
	@PackQuantity INT,
	@Quantity INT,
	@Total INT,
	@Price DECIMAL(18,2),
	@Category INT,
	@Rating INT,
	@Active BIT,
	@Trending INT,
	@UserBuyCount INT,
	@Return INT,
	@BestSeller BIT,
	@DeliveryDate INT,
	@Offer NVARCHAR(100),
	@OrderBy INT,
	@UserId BIGINT,
	@ModifiedBy BIGINT
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		-- Check if product exists and belongs to the tenant
		IF NOT EXISTS (SELECT 1 FROM Products WHERE ProductId = @ProductId AND TenantId = @TenantId)
		BEGIN
			RAISERROR('Product not found or does not belong to this tenant.', 16, 1);
			RETURN;
		END

		-- Check if product code already exists for another product of this tenant
		IF EXISTS (
			SELECT 1 
			FROM Products 
			WHERE TenantId = @TenantId 
				AND ProductCode = @ProductCode 
				AND ProductId != @ProductId
		)
		BEGIN
			RAISERROR('Product code already exists for another product.', 16, 1);
			RETURN;
		END

		-- Update product
		UPDATE Products
		SET
			ProductName = @ProductName,
			ProductDescription = @ProductDescription,
			ProductCode = @ProductCode,
			FullDescription = @FullDescription,
			Specification = @Specification,
			Story = @Story,
			PackQuantity = @PackQuantity,
			Quantity = @Quantity,
			Total = @Total,
			Price = @Price,
			Category = @Category,
			Rating = @Rating,
			Active = @Active,
			Trending = @Trending,
			UserBuyCount = @UserBuyCount,
			[Return] = @Return,
			BestSeller = @BestSeller,
			DeliveryDate = @DeliveryDate,
			Offer = @Offer,
			OrderBy = @OrderBy,
			UserId = @UserId,
			Modified = GETUTCDATE(),
			ModifiedBy = @ModifiedBy
		WHERE ProductId = @ProductId
			AND TenantId = @TenantId;

		-- Return success
		SELECT @ProductId AS ProductId;

		COMMIT TRANSACTION;
	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END


CREATE PROCEDURE [dbo].[SP_DELETE_PRODUCT]
	@ProductId BIGINT,
	@TenantId BIGINT,
	@UserId BIGINT
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		-- Check if product exists and belongs to the tenant
		IF NOT EXISTS (SELECT 1 FROM Products WHERE ProductId = @ProductId AND TenantId = @TenantId)
		BEGIN
			RAISERROR('Product not found or does not belong to this tenant.', 16, 1);
			RETURN;
		END

		-- Check if product is referenced in any orders or carts
		IF EXISTS (
			SELECT 1 
			FROM CartItems 
			WHERE ProductId = @ProductId AND Active = 1
			   OR EXISTS (SELECT 1 FROM OrderItems WHERE ProductId = @ProductId AND Active = 1)
		)
		BEGIN
			-- Soft delete - just mark as inactive
			UPDATE Products
			SET 
				Active = 0,
				Modified = GETUTCDATE(),
				ModifiedBy = @UserId
			WHERE ProductId = @ProductId
				AND TenantId = @TenantId;
		END
		ELSE
		BEGIN
			-- Hard delete - first delete related records
			DELETE FROM ProductImages WHERE ProductId = @ProductId;
			DELETE FROM ProductReviews WHERE ProductId = @ProductId;
			DELETE FROM ProductWishList WHERE ProductId = @ProductId;

			-- Then delete the product
			DELETE FROM Products 
			WHERE ProductId = @ProductId 
				AND TenantId = @TenantId;
		END

		-- Return success
		SELECT @ProductId AS ProductId;

		COMMIT TRANSACTION;
	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END

CREATE PROCEDURE [dbo].[SP_GET_ALL_CATEGORIES] @TenantId BIGINT = NULL AS BEGIN SET NOCOUNT ON;
BEGIN TRY -- Get all categories with optional tenant filtering SELECT c.CategoryId, c.CategoryName AS Category, c.Active, c.HasSubMenu AS SubMenu, c.Created, c.Modified, c.OrderBy, c.Description, c.Icon, c.ParentCategoryId, c.TenantId FROM Categories c WHERE (@TenantId IS NULL OR c.TenantId = @TenantId) AND c.Active = 1 ORDER BY c.OrderBy, c.CategoryName;
END TRY BEGIN CATCH DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH END


CREATE PROCEDURE [dbo].[SP_ADD_CATEGORY]
	@TenantId BIGINT,
	@CategoryName NVARCHAR(255),
	@Description NVARCHAR(MAX) = NULL,
	@Active BIT = 1,
	@ParentCategoryId BIGINT = NULL,
	@OrderBy INT = 0,
	@Icon NVARCHAR(255) = NULL,
	@HasSubMenu BIT = 0,
	@Link NVARCHAR(500) = NULL,
	@UserId BIGINT
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		-- Check if category name already exists for this tenant
		IF EXISTS (SELECT 1 FROM Categories WHERE CategoryName = @CategoryName AND TenantId = @TenantId)
		BEGIN
			RAISERROR('Category name already exists for this tenant.', 16, 1);
			RETURN;
		END

		-- Validate parent category if provided
		IF @ParentCategoryId IS NOT NULL
		BEGIN
			IF NOT EXISTS (SELECT 1 FROM Categories WHERE CategoryId = @ParentCategoryId AND TenantId = @TenantId)
			BEGIN
				RAISERROR('Parent category does not exist.', 16, 1);
				RETURN;
			END
		END

		-- Insert new category
		INSERT INTO Categories (
			TenantId,
			CategoryName,
			Description,
			Active,
			ParentCategoryId,
			OrderBy,
			Icon,
			HasSubMenu,
			Link,
			Created,
			CreatedBy,
			Modified,
			ModifiedBy
		) VALUES (
			@TenantId,
			@CategoryName,
			@Description,
			@Active,
			@ParentCategoryId,
			@OrderBy,
			@Icon,
			@HasSubMenu,
			@Link,
			GETUTCDATE(),
			@UserId,
			GETUTCDATE(),
			@UserId
		);

		-- Return the new category ID
		SELECT SCOPE_IDENTITY() AS CategoryId;

		COMMIT TRANSACTION;
	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END


CREATE PROCEDURE [dbo].[SP_UPDATE_CATEGORY]
	@CategoryId BIGINT,
	@TenantId BIGINT,
	@CategoryName NVARCHAR(255),
	@Description NVARCHAR(MAX) = NULL,
	@Active BIT = 1,
	@ParentCategoryId BIGINT = NULL,
	@OrderBy INT = 0,
	@Icon NVARCHAR(255) = NULL,
	@HasSubMenu BIT = 0,
	@Link NVARCHAR(500) = NULL,
	@UserId BIGINT
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		-- Check if category exists and belongs to the tenant
		IF NOT EXISTS (SELECT 1 FROM Categories WHERE CategoryId = @CategoryId AND TenantId = @TenantId)
		BEGIN
			RAISERROR('Category not found or does not belong to this tenant.', 16, 1);
			RETURN;
		END

		-- Check if new category name already exists for this tenant (excluding current category)
		IF EXISTS (SELECT 1 FROM Categories WHERE CategoryName = @CategoryName AND TenantId = @TenantId AND CategoryId != @CategoryId)
		BEGIN
			RAISERROR('Category name already exists for this tenant.', 16, 1);
			RETURN;
		END

		-- Validate parent category if provided
		IF @ParentCategoryId IS NOT NULL
		BEGIN
			IF NOT EXISTS (SELECT 1 FROM Categories WHERE CategoryId = @ParentCategoryId AND TenantId = @TenantId)
			BEGIN
				RAISERROR('Parent category does not exist.', 16, 1);
				RETURN;
			END

			-- Prevent circular reference (category cannot be its own parent or grandparent)
			IF @ParentCategoryId = @CategoryId
			BEGIN
				RAISERROR('Category cannot be its own parent.', 16, 1);
				RETURN;
			END
		END

		-- Update category
		UPDATE Categories
		SET 
			CategoryName = @CategoryName,
			Description = @Description,
			Active = @Active,
			ParentCategoryId = @ParentCategoryId,
			OrderBy = @OrderBy,
			Icon = @Icon,
			HasSubMenu = @HasSubMenu,
			Link = @Link,
			Modified = GETUTCDATE(),
			ModifiedBy = @UserId
		WHERE CategoryId = @CategoryId
			AND TenantId = @TenantId;

		-- Return the updated category ID
		SELECT @CategoryId AS CategoryId;

		COMMIT TRANSACTION;
	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END

CREATE PROCEDURE [dbo].[SP_GET_MENU_MASTER] @TenantId BIGINT = NULL AS BEGIN SET NOCOUNT ON;
BEGIN TRY -- Get menu master with categories SELECT m.MenuId, m.MenuName, m.OrderBy, m.Active, m.Image, m.SubMenu, m.TenantId, m.Created, m.Modified, -- Category information c.CategoryId, c.CategoryName AS Category, c.Active AS CategoryActive, c.OrderBy AS CategoryOrderBy, c.Icon AS CategoryIcon, c.Description AS CategoryDescription FROM MenuMaster m LEFT JOIN Categories c ON m.MenuId = c.MenuId AND c.Active = 1 AND (@TenantId IS NULL OR c.TenantId = @TenantId) WHERE m.Active = 1 AND (@TenantId IS NULL OR m.TenantId = @TenantId) ORDER BY m.OrderBy, m.MenuName, c.OrderBy, c.CategoryName;
END TRY BEGIN CATCH DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH END


CREATE PROCEDURE [dbo].[SP_USER_LOGIN]
	@EmailOrPhone NVARCHAR(255),
	@Password NVARCHAR(255),
	@RememberMe BIT = 0
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		DECLARE @UserId BIGINT = NULL;
		DECLARE @IsActive BIT = 0;
		DECLARE @PasswordHash NVARCHAR(255);
		DECLARE @Salt NVARCHAR(255);
		DECLARE @LoginAttempts INT = 0;
		DECLARE @AccountLocked BIT = 0;
		DECLARE @LastLoginAttempt DATETIME;

		-- Find user by email or phone
		SELECT 
			@UserId = UserId,
			@IsActive = Active,
			@PasswordHash = PasswordHash,
			@Salt = Salt,
			@LoginAttempts = LoginAttempts,
			@AccountLocked = AccountLocked,
			@LastLoginAttempt = LastLoginAttempt
		FROM Users 
		WHERE (Email = @EmailOrPhone OR Phone = @EmailOrPhone)
			AND Active = 1;

		-- Check if user exists
		IF @UserId IS NULL
		BEGIN
			RAISERROR('Invalid email/phone or password.', 16, 1);
			RETURN;
		END

		-- Check if account is locked
		IF @AccountLocked = 1
		BEGIN
			-- Check if lock period has expired (30 minutes)
			IF DATEDIFF(MINUTE, @LastLoginAttempt, GETUTCDATE()) < 30
			BEGIN
				RAISERROR('Account is temporarily locked due to multiple failed login attempts. Please try again later.', 16, 1);
				RETURN;
			END
			ELSE
			BEGIN
				-- Unlock account
				UPDATE Users 
				SET AccountLocked = 0, LoginAttempts = 0 
				WHERE UserId = @UserId;
				SET @AccountLocked = 0;
				SET @LoginAttempts = 0;
			END
		END

		-- Verify password (In real implementation, you would hash the input password with salt)
		-- For demo purposes, we'll do a simple comparison
		IF @PasswordHash != HASHBYTES('SHA256', @Password + @Salt)
		BEGIN
			-- Increment login attempts
			SET @LoginAttempts = @LoginAttempts + 1;

			-- Lock account after 5 failed attempts
			IF @LoginAttempts >= 5
			BEGIN
				UPDATE Users 
				SET LoginAttempts = @LoginAttempts, 
					AccountLocked = 1, 
					LastLoginAttempt = GETUTCDATE()
				WHERE UserId = @UserId;

				RAISERROR('Account has been locked due to multiple failed login attempts.', 16, 1);
				RETURN;
			END
			ELSE
			BEGIN
				UPDATE Users 
				SET LoginAttempts = @LoginAttempts, 
					LastLoginAttempt = GETUTCDATE()
				WHERE UserId = @UserId;

				RAISERROR('Invalid email/phone or password.', 16, 1);
				RETURN;
			END
		END

		-- Successful login - reset attempts and update last login
		UPDATE Users 
		SET LoginAttempts = 0, 
			LastLogin = GETUTCDATE(),
			LastLoginAttempt = GETUTCDATE(),
			AccountLocked = 0
		WHERE UserId = @UserId;

		-- Return user information
		SELECT 
			u.UserId,
			u.FirstName,
			u.LastName,
			u.Email,
			u.Phone,
			u.Active,
			u.TenantId,
			u.LastLogin,
			ur.RoleId,
			r.RoleName,
			r.RoleDescription,
			@RememberMe AS RememberMe
		FROM Users u
		LEFT JOIN UserRoles ur ON u.UserId = ur.UserId
		LEFT JOIN Roles r ON ur.RoleId = r.RoleId
		WHERE u.UserId = @UserId
			AND u.Active = 1;

	END TRY
	BEGIN CATCH
		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END


CREATE PROCEDURE [dbo].[SP_USER_REGISTER]
	@Name NVARCHAR(255),
	@Email NVARCHAR(255),
	@Phone NVARCHAR(50),
	@Password NVARCHAR(255),
	@TenantId BIGINT = 1,
	@AgreeToTerms BIT = 1
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		DECLARE @UserId BIGINT;
		DECLARE @Salt NVARCHAR(255);
		DECLARE @PasswordHash NVARCHAR(255);
		DECLARE @DefaultRoleId BIGINT = 2; -- Assuming 2 is the default user role

		-- Check if email already exists
		IF EXISTS (SELECT 1 FROM Users WHERE Email = @Email)
		BEGIN
			RAISERROR('Email address is already registered.', 16, 1);
			RETURN;
		END

		-- Check if phone already exists
		IF EXISTS (SELECT 1 FROM Users WHERE Phone = @Phone)
		BEGIN
			RAISERROR('Phone number is already registered.', 16, 1);
			RETURN;
		END

		-- Validate terms agreement
		IF @AgreeToTerms = 0
		BEGIN
			RAISERROR('You must agree to the terms and conditions.', 16, 1);
			RETURN;
		END

		-- Generate salt and hash password
		SET @Salt = CONVERT(NVARCHAR(255), NEWID());
		SET @PasswordHash = CONVERT(NVARCHAR(255), HASHBYTES('SHA256', @Password + @Salt), 2);

		-- Parse name into first and last name
		DECLARE @FirstName NVARCHAR(255);
		DECLARE @LastName NVARCHAR(255);
		DECLARE @SpaceIndex INT = CHARINDEX(' ', @Name);

		IF @SpaceIndex > 0
		BEGIN
			SET @FirstName = LEFT(@Name, @SpaceIndex - 1);
			SET @LastName = RIGHT(@Name, LEN(@Name) - @SpaceIndex);
		END
		ELSE
		BEGIN
			SET @FirstName = @Name;
			SET @LastName = '';
		END

		-- Insert new user
		INSERT INTO Users (
			FirstName,
			LastName,
			Email,
			Phone,
			PasswordHash,
			Salt,
			TenantId,
			Active,
			EmailVerified,
			PhoneVerified,
			LoginAttempts,
			AccountLocked,
			CreatedAt,
			UpdatedAt,
			AgreeToTerms
		) VALUES (
			@FirstName,
			@LastName,
			@Email,
			@Phone,
			@PasswordHash,
			@Salt,
			@TenantId,
			1, -- Active
			0, -- EmailVerified
			0, -- PhoneVerified
			0, -- LoginAttempts
			0, -- AccountLocked
			GETUTCDATE(),
			GETUTCDATE(),
			@AgreeToTerms
		);

		-- Get the new user ID
		SET @UserId = SCOPE_IDENTITY();

		-- Assign default role to user
		INSERT INTO UserRoles (UserId, RoleId, CreatedAt)
		VALUES (@UserId, @DefaultRoleId, GETUTCDATE());

		-- Return user information with role
		SELECT 
			u.UserId,
			u.FirstName,
			u.LastName,
			u.Email,
			u.Phone,
			u.Active,
			u.TenantId,
			u.EmailVerified,
			u.PhoneVerified,
			u.CreatedAt,
			ur.RoleId,
			r.RoleName,
			r.RoleDescription
		FROM Users u
		LEFT JOIN UserRoles ur ON u.UserId = ur.UserId
		LEFT JOIN Roles r ON ur.RoleId = r.RoleId
		WHERE u.UserId = @UserId;

		COMMIT TRANSACTION;

	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END

CREATE PROCEDURE [dbo].[SP_USER_LOGOUT] @UserId BIGINT, @Token NVARCHAR(MAX) = NULL, @RefreshToken NVARCHAR(MAX) = NULL, @DeviceId NVARCHAR(255) = NULL, @LogoutFromAllDevices BIT = 0 AS BEGIN SET NOCOUNT ON;
BEGIN TRY BEGIN TRANSACTION;
-- Validate user exists and is active IF NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1) BEGIN RAISERROR('User not found or inactive.', 16, 1); RETURN; END
-- If logout from all devices is requested IF @LogoutFromAllDevices = 1 BEGIN -- Invalidate all tokens for this user UPDATE UserTokens SET IsRevoked = 1, RevokedAt = GETUTCDATE(), RevokedReason = 'Logout from all devices' WHERE UserId = @UserId AND IsRevoked = 0;
-- Clear remember me sessions UPDATE Users SET RememberMeToken = NULL, RememberMeExpiry = NULL, LastLogout = GETUTCDATE() WHERE UserId = @UserId; END ELSE BEGIN -- Logout from specific device/token IF @Token IS NOT NULL BEGIN -- Invalidate specific token UPDATE UserTokens SET IsRevoked = 1, RevokedAt = GETUTCDATE(), RevokedReason = 'User logout' WHERE UserId = @UserId AND (Token = @Token OR RefreshToken = @RefreshToken) AND IsRevoked = 0; END
IF @DeviceId IS NOT NULL BEGIN -- Invalidate tokens for specific device UPDATE UserTokens SET IsRevoked = 1, RevokedAt = GETUTCDATE(), RevokedReason = 'Device logout' WHERE UserId = @UserId AND DeviceId = @DeviceId AND IsRevoked = 0; END
-- Update last logout time UPDATE Users SET LastLogout = GETUTCDATE() WHERE UserId = @UserId; END
-- Log the logout activity INSERT INTO UserActivityLog ( UserId, ActivityType, ActivityDescription, IPAddress, UserAgent, DeviceId, CreatedAt ) VALUES ( @UserId, 'LOGOUT', CASE WHEN @LogoutFromAllDevices = 1 THEN 'Logout from all devices' WHEN @DeviceId IS NOT NULL THEN 'Logout from device: ' + @DeviceId ELSE 'User logout' END, NULL, -- IP Address would be passed from application NULL, -- User Agent would be passed from application @DeviceId, GETUTCDATE() );
-- Return success status SELECT @UserId AS UserId, 'Logged out successfully' AS Message, GETUTCDATE() AS LogoutTime, @LogoutFromAllDevices AS LogoutFromAllDevices;
COMMIT TRANSACTION;
END TRY BEGIN CATCH IF @@TRANCOUNT > 0 ROLLBACK TRANSACTION;
DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH END

CREATE PROCEDURE [dbo].[SP_GET_USER_PROFILE] @UserId BIGINT, @TenantId BIGINT = NULL AS BEGIN SET NOCOUNT ON;
BEGIN TRY -- Validate user exists and is active IF NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1) BEGIN RAISERROR('User not found or inactive.', 16, 1); RETURN; END
-- Additional tenant validation if provided IF @TenantId IS NOT NULL BEGIN IF NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND TenantId = @TenantId AND Active = 1) BEGIN RAISERROR('User not found in the specified tenant.', 16, 1); RETURN; END END
-- Get user profile information SELECT u.UserId, u.FirstName, u.LastName, u.Email, u.Phone, u.Active, u.TenantId, u.EmailVerified, u.PhoneVerified, u.CreatedAt, u.UpdatedAt, u.LastLogin, u.LastLogout, u.ProfilePicture, u.DateOfBirth, u.Gender, u.Timezone, u.Language, u.Country, u.City, u.State, u.PostalCode, u.Bio, u.Website, u.CompanyName, u.JobTitle, u.PreferredContactMethod, u.NotificationSettings, u.PrivacySettings, -- User roles ur.RoleId, r.RoleName, r.RoleDescription, -- User addresses addr.AddressId, addr.AddressType, addr.Street, addr.City AS AddressCity, addr.State AS AddressState, addr.PostalCode AS AddressPostalCode, addr.Country AS AddressCountry, addr.IsDefault AS IsDefaultAddress, -- User preferences up.PreferenceKey, up.PreferenceValue, up.PreferenceType FROM Users u LEFT JOIN UserRoles ur ON u.UserId = ur.UserId LEFT JOIN Roles r ON ur.RoleId = r.RoleId LEFT JOIN UserAddresses addr ON u.UserId = addr.UserId AND addr.Active = 1 LEFT JOIN UserPreferences up ON u.UserId = up.UserId AND up.Active = 1 WHERE u.UserId = @UserId AND u.Active = 1 AND (@TenantId IS NULL OR u.TenantId = @TenantId) ORDER BY ur.RoleId, addr.IsDefault DESC, up.PreferenceKey;
-- Get user statistics (optional) SELECT 'LOGIN_COUNT' AS StatType, COUNT(*) AS StatValue FROM UserActivityLog WHERE UserId = @UserId AND ActivityType = 'LOGIN' AND CreatedAt >= DATEADD(MONTH, -12, GETUTCDATE())
UNION ALL
SELECT 'LAST_ACTIVITY' AS StatType, DATEDIFF(DAY, MAX(CreatedAt), GETUTCDATE()) AS StatValue FROM UserActivityLog WHERE UserId = @UserId
UNION ALL
SELECT 'PROFILE_COMPLETION' AS StatType, CASE WHEN ProfilePicture IS NOT NULL THEN 10 ELSE 0 END + CASE WHEN DateOfBirth IS NOT NULL THEN 10 ELSE 0 END + CASE WHEN Bio IS NOT NULL THEN 10 ELSE 0 END + CASE WHEN Phone IS NOT NULL THEN 10 ELSE 0 END + CASE WHEN EmailVerified = 1 THEN 20 ELSE 0 END + CASE WHEN PhoneVerified = 1 THEN 20 ELSE 0 END + 20 AS StatValue -- Base score for having name and email FROM Users WHERE UserId = @UserId;
END TRY BEGIN CATCH DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH END


CREATE PROCEDURE [dbo].[SP_UPDATE_USER_PROFILE]
	@UserId BIGINT,
	@Name NVARCHAR(255) = NULL,
	@Phone NVARCHAR(50) = NULL,
	@DateOfBirth DATETIME = NULL,
	@Gender NVARCHAR(20) = NULL,
	@Bio NVARCHAR(MAX) = NULL,
	@Website NVARCHAR(255) = NULL,
	@CompanyName NVARCHAR(255) = NULL,
	@JobTitle NVARCHAR(255) = NULL,
	@Country NVARCHAR(100) = NULL,
	@City NVARCHAR(100) = NULL,
	@State NVARCHAR(100) = NULL,
	@PostalCode NVARCHAR(20) = NULL,
	@Timezone NVARCHAR(100) = NULL,
	@Language NVARCHAR(10) = NULL,
	@PreferredContactMethod NVARCHAR(50) = NULL,
	-- Address Information
	@AddressStreet NVARCHAR(255) = NULL,
	@AddressCity NVARCHAR(100) = NULL,
	@AddressState NVARCHAR(100) = NULL,
	@AddressZipCode NVARCHAR(20) = NULL,
	@AddressCountry NVARCHAR(100) = NULL,
	@AddressType NVARCHAR(50) = 'Home',
	@UpdateAddressIfExists BIT = 1
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		-- Validate user exists and is active
		IF NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1)
		BEGIN
			RAISERROR('User not found or inactive.', 16, 1);
			RETURN;
		END

		-- Parse name into first and last name if provided
		DECLARE @FirstName NVARCHAR(255) = NULL;
		DECLARE @LastName NVARCHAR(255) = NULL;

		IF @Name IS NOT NULL
		BEGIN
			DECLARE @SpaceIndex INT = CHARINDEX(' ', @Name);
			IF @SpaceIndex > 0
			BEGIN
				SET @FirstName = LEFT(@Name, @SpaceIndex - 1);
				SET @LastName = RIGHT(@Name, LEN(@Name) - @SpaceIndex);
			END
			ELSE
			BEGIN
				SET @FirstName = @Name;
				SET @LastName = '';
			END
		END

		-- Check if phone number already exists for another user
		IF @Phone IS NOT NULL AND EXISTS (SELECT 1 FROM Users WHERE Phone = @Phone AND UserId != @UserId)
		BEGIN
			RAISERROR('Phone number is already registered to another user.', 16, 1);
			RETURN;
		END

		-- Update user profile
		UPDATE Users
		SET 
			FirstName = ISNULL(@FirstName, FirstName),
			LastName = ISNULL(@LastName, LastName),
			Phone = ISNULL(@Phone, Phone),
			DateOfBirth = ISNULL(@DateOfBirth, DateOfBirth),
			Gender = ISNULL(@Gender, Gender),
			Bio = ISNULL(@Bio, Bio),
			Website = ISNULL(@Website, Website),
			CompanyName = ISNULL(@CompanyName, CompanyName),
			JobTitle = ISNULL(@JobTitle, JobTitle),
			Country = ISNULL(@Country, Country),
			City = ISNULL(@City, City),
			State = ISNULL(@State, State),
			PostalCode = ISNULL(@PostalCode, PostalCode),
			Timezone = ISNULL(@Timezone, Timezone),
			Language = ISNULL(@Language, Language),
			PreferredContactMethod = ISNULL(@PreferredContactMethod, PreferredContactMethod),
			UpdatedAt = GETUTCDATE()
		WHERE UserId = @UserId;

		-- Handle address update/creation if address information is provided
		IF @AddressStreet IS NOT NULL OR @AddressCity IS NOT NULL OR @AddressState IS NOT NULL 
			OR @AddressZipCode IS NOT NULL OR @AddressCountry IS NOT NULL
		BEGIN
			DECLARE @ExistingAddressId BIGINT = NULL;

			-- Check if user has an existing address of the specified type
			SELECT @ExistingAddressId = AddressId 
			FROM UserAddresses 
			WHERE UserId = @UserId 
				AND AddressType = @AddressType 
				AND Active = 1;

			IF @ExistingAddressId IS NOT NULL AND @UpdateAddressIfExists = 1
			BEGIN
				-- Update existing address
				UPDATE UserAddresses
				SET 
					Street = ISNULL(@AddressStreet, Street),
					City = ISNULL(@AddressCity, City),
					State = ISNULL(@AddressState, State),
					PostalCode = ISNULL(@AddressZipCode, PostalCode),
					Country = ISNULL(@AddressCountry, Country),
					UpdatedAt = GETUTCDATE()
				WHERE AddressId = @ExistingAddressId;
			END
			ELSE IF @ExistingAddressId IS NULL
			BEGIN
				-- Create new address
				INSERT INTO UserAddresses (
					UserId,
					AddressType,
					Street,
					City,
					State,
					PostalCode,
					Country,
					IsDefault,
					Active,
					CreatedAt,
					UpdatedAt
				) VALUES (
					@UserId,
					@AddressType,
					@AddressStreet,
					@AddressCity,
					@AddressState,
					@AddressZipCode,
					@AddressCountry,
					1, -- Set as default if it's the first address
					1,
					GETUTCDATE(),
					GETUTCDATE()
				);
			END
		END

		-- Log the profile update activity
		INSERT INTO UserActivityLog (
			UserId,
			ActivityType,
			ActivityDescription,
			CreatedAt
		) VALUES (
			@UserId,
			'PROFILE_UPDATE',
			'User profile updated',
			GETUTCDATE()
		);

		-- Return success status
		SELECT 
			@UserId AS UserId,
			'Profile updated successfully' AS Message,
			GETUTCDATE() AS UpdatedAt;

		COMMIT TRANSACTION;

	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END


CREATE PROCEDURE [dbo].[SP_RESET_PASSWORD]
	@ResetToken NVARCHAR(255),
	@NewPassword NVARCHAR(255),
	@IpAddress NVARCHAR(45) = NULL,
	@UserAgent NVARCHAR(500) = NULL
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		DECLARE @UserId BIGINT = NULL;
		DECLARE @TokenExpiry DATETIME = NULL;
		DECLARE @TokenUsed BIT = 0;
		DECLARE @Email NVARCHAR(255) = NULL;
		DECLARE @Salt NVARCHAR(50) = NULL;
		DECLARE @HashedPassword NVARCHAR(255) = NULL;
		DECLARE @CurrentTime DATETIME = GETUTCDATE();

		-- Validate reset token and get user information
		SELECT 
			@UserId = prt.UserId,
			@TokenExpiry = prt.ExpiresAt,
			@TokenUsed = prt.IsUsed,
			@Email = u.Email
		FROM PasswordResetTokens prt
		INNER JOIN Users u ON prt.UserId = u.UserId
		WHERE prt.ResetToken = @ResetToken
			AND u.Active = 1;

		-- Check if token exists
		IF @UserId IS NULL
		BEGIN
			RAISERROR('Invalid or expired reset token.', 16, 1);
			RETURN;
		END

		-- Check if token has already been used
		IF @TokenUsed = 1
		BEGIN
			RAISERROR('Reset token has already been used.', 16, 1);
			RETURN;
		END

		-- Check if token has expired
		IF @TokenExpiry < @CurrentTime
		BEGIN
			RAISERROR('Reset token has expired. Please request a new password reset.', 16, 1);
			RETURN;
		END

		-- Generate a new salt and hash the password
		SET @Salt = RIGHT(NEWID(), 12);
		SET @HashedPassword = CONVERT(NVARCHAR(255), HASHBYTES('SHA256', @NewPassword + @Salt), 2);

		-- Update user password
		UPDATE Users
		SET 
			PasswordHash = @HashedPassword,
			Salt = @Salt,
			PasswordChangedAt = @CurrentTime,
			LastPasswordReset = @CurrentTime,
			UpdatedAt = @CurrentTime,
			-- Reset failed login attempts since password was successfully reset
			LoginAttempts = 0,
			AccountLocked = 0
		WHERE UserId = @UserId;

		-- Mark the reset token as used
		UPDATE PasswordResetTokens
		SET 
			IsUsed = 1,
			UsedAt = @CurrentTime,
			UpdatedAt = @CurrentTime
		WHERE ResetToken = @ResetToken;

		-- Invalidate all existing sessions/tokens for this user (for security)
		UPDATE UserSessions
		SET 
			IsActive = 0,
			LoggedOutAt = @CurrentTime,
			UpdatedAt = @CurrentTime
		WHERE UserId = @UserId AND IsActive = 1;

		-- Log the password reset activity
		INSERT INTO UserActivityLog (
			UserId,
			ActivityType,
			ActivityDescription,
			IpAddress,
			UserAgent,
			CreatedAt
		) VALUES (
			@UserId,
			'PASSWORD_RESET',
			'Password reset successfully using reset token',
			@IpAddress,
			@UserAgent,
			@CurrentTime
		);

		-- Optional: Send notification email (placeholder for email service integration)
		-- This would typically trigger an email notification about successful password reset
		INSERT INTO UserNotifications (
			UserId,
			NotificationType,
			Title,
			Message,
			IsRead,
			CreatedAt
		) VALUES (
			@UserId,
			'SECURITY_ALERT',
			'Password Reset Successful',
			'Your password has been successfully reset. If you did not perform this action, please contact support immediately.',
			0,
			@CurrentTime
		);

		-- Return success information
		SELECT 
			@UserId AS UserId,
			@Email AS Email,
			'Password reset successfully' AS Message,
			@CurrentTime AS ResetDate;

		COMMIT TRANSACTION;

	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		-- Log the failed password reset attempt
		IF @UserId IS NOT NULL
		BEGIN
			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				@UserId,
				'PASSWORD_RESET_FAILED',
				'Failed password reset attempt: ' + @ErrorMessage,
				@IpAddress,
				@UserAgent,
				GETUTCDATE()
			);
		END

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END

CREATE PROCEDURE [dbo].[SP_GET_USER_CART] @UserId BIGINT, @TenantId BIGINT = NULL AS BEGIN SET NOCOUNT ON;
BEGIN TRY -- Validate user exists and is active IF NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1) BEGIN RAISERROR('User not found or inactive.', 16, 1); RETURN; END
-- Get cart items with full product details SELECT -- Cart Information c.CartId, c.UserId, c.TenantId, c.Quantity, c.AddedDate, c.UpdatedDate, c.SessionId,
-- Product Information p.ProductId, p.ProductName, p.ProductDescription, p.ProductCode, p.FullDescription, p.Specification, p.Story, p.PackQuantity, p.Quantity AS ProductAvailableQuantity, p.Total AS ProductTotal, p.Price, p.Category AS CategoryId, p.Rating, p.Active AS ProductActive, p.Trending, p.UserBuyCount, p.Return AS ReturnPolicy, p.Created AS ProductCreated, p.Modified AS ProductModified, p.InStock, p.BestSeller, p.DeliveryDate, p.Offer, p.OrderBy AS ProductOrderBy, p.UserId AS ProductUserId, p.Overview, p.LongDescription,
-- Category Information cat.Category AS CategoryName, cat.Active AS CategoryActive, cat.OrderBy AS CategoryOrderBy, cat.Description AS CategoryDescription, cat.Icon AS CategoryIcon, cat.SubMenu AS CategorySubMenu,
-- Calculated Fields (c.Quantity * p.Price) AS ItemTotal, CASE WHEN p.Quantity >= c.Quantity THEN 1 ELSE 0 END AS IsAvailable,
-- Product Images (will be handled in a separate query or joined) pi.ImageId, pi.Poster AS ImageUrl, pi.Main AS IsMainImage, pi.Active AS ImageActive, pi.OrderBy AS ImageOrderBy
FROM CartItems c INNER JOIN Products p ON c.ProductId = p.ProductId LEFT JOIN ProductCategories cat ON p.Category = cat.CategoryId LEFT JOIN ProductImages pi ON p.ProductId = pi.ProductId AND pi.Active = 1 WHERE c.UserId = @UserId AND c.Active = 1 AND p.Active = 1 AND (@TenantId IS NULL OR c.TenantId = @TenantId) ORDER BY c.AddedDate DESC, pi.OrderBy ASC;
-- Get cart summary SELECT COUNT(*) AS TotalItems, SUM(c.Quantity) AS TotalQuantity, SUM(c.Quantity * p.Price) AS TotalAmount, SUM(CASE WHEN p.Quantity >= c.Quantity THEN (c.Quantity * p.Price) ELSE 0 END) AS AvailableItemsTotal, COUNT(CASE WHEN p.Quantity < c.Quantity THEN 1 END) AS UnavailableItems FROM CartItems c INNER JOIN Products p ON c.ProductId = p.ProductId WHERE c.UserId = @UserId AND c.Active = 1 AND p.Active = 1 AND (@TenantId IS NULL OR c.TenantId = @TenantId);
-- Get related/recommended products (optional - based on cart items) SELECT TOP 10 p.ProductId, p.ProductName, p.Price, p.Rating, p.BestSeller, p.Offer, pi.Poster AS ImageUrl FROM Products p LEFT JOIN ProductImages pi ON p.ProductId = pi.ProductId AND pi.Main = 1 AND pi.Active = 1 WHERE p.Active = 1 AND p.ProductId NOT IN ( SELECT c.ProductId FROM CartItems c WHERE c.UserId = @UserId AND c.Active = 1 ) AND p.Category IN ( SELECT DISTINCT p2.Category FROM CartItems c2 INNER JOIN Products p2 ON c2.ProductId = p2.ProductId WHERE c2.UserId = @UserId AND c2.Active = 1 ) AND (@TenantId IS NULL OR p.TenantId = @TenantId) ORDER BY p.Rating DESC, p.UserBuyCount DESC;
END TRY BEGIN CATCH DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH END


CREATE PROCEDURE [dbo].[SP_ADD_ITEM_TO_CART]
	@UserId BIGINT,
	@ProductId BIGINT,
	@Quantity INT,
	@TenantId BIGINT = NULL,
	@SessionId NVARCHAR(255) = NULL,
	@IpAddress NVARCHAR(45) = NULL,
	@UserAgent NVARCHAR(500) = NULL
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		DECLARE @ExistingCartId BIGINT = NULL;
		DECLARE @ExistingQuantity INT = 0;
		DECLARE @ProductPrice DECIMAL(18,2) = 0;
		DECLARE @ProductName NVARCHAR(255) = '';
		DECLARE @AvailableStock INT = 0;
		DECLARE @ProductActive BIT = 0;
		DECLARE @CurrentTime DATETIME = GETUTCDATE();
		DECLARE @NewQuantity INT = 0;

		-- Validate user exists and is active
		IF NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1)
		BEGIN
			RAISERROR('User not found or inactive.', 16, 1);
			RETURN;
		END

		-- Validate product exists, is active, and get product details
		SELECT 
			@ProductPrice = Price,
			@ProductName = ProductName,
			@AvailableStock = Quantity,
			@ProductActive = Active
		FROM Products 
		WHERE ProductId = @ProductId
			AND (@TenantId IS NULL OR TenantId = @TenantId);

		IF @ProductActive IS NULL OR @ProductActive = 0
		BEGIN
			RAISERROR('Product not found or inactive.', 16, 1);
			RETURN;
		END

		-- Validate quantity is positive
		IF @Quantity <= 0
		BEGIN
			RAISERROR('Quantity must be greater than 0.', 16, 1);
			RETURN;
		END

		-- Check if item already exists in cart
		SELECT 
			@ExistingCartId = CartId,
			@ExistingQuantity = Quantity
		FROM CartItems 
		WHERE UserId = @UserId 
			AND ProductId = @ProductId 
			AND Active = 1
			AND (@TenantId IS NULL OR TenantId = @TenantId);

		-- Calculate new quantity
		SET @NewQuantity = @ExistingQuantity + @Quantity;

		-- Check stock availability
		IF @NewQuantity > @AvailableStock
		BEGIN
			RAISERROR('Insufficient stock. Available quantity: %d, Requested quantity: %d.', 16, 1, @AvailableStock, @NewQuantity);
			RETURN;
		END

		IF @ExistingCartId IS NOT NULL
		BEGIN
			-- Update existing cart item
			UPDATE CartItems
			SET 
				Quantity = @NewQuantity,
				UpdatedDate = @CurrentTime,
				SessionId = ISNULL(@SessionId, SessionId)
			WHERE CartId = @ExistingCartId;

			-- Log the cart update activity
			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				@UserId,
				'CART_UPDATE',
				'Updated cart item: ' + @ProductName + ' (Quantity: ' + CAST(@NewQuantity AS VARCHAR(10)) + ')',
				@IpAddress,
				@UserAgent,
				@CurrentTime
			);

			-- Return updated cart item info
			SELECT 
				@ExistingCartId AS CartId,
				@UserId AS UserId,
				@ProductId AS ProductId,
				@ProductName AS ProductName,
				@NewQuantity AS Quantity,
				@ProductPrice AS Price,
				(@NewQuantity * @ProductPrice) AS ItemTotal,
				'Product quantity updated in cart' AS Message,
				@CurrentTime AS UpdatedDate;
		END
		ELSE
		BEGIN
			-- Insert new cart item
			INSERT INTO CartItems (
				UserId,
				ProductId,
				Quantity,
				TenantId,
				SessionId,
				Active,
				AddedDate,
				UpdatedDate
			) VALUES (
				@UserId,
				@ProductId,
				@Quantity,
				@TenantId,
				@SessionId,
				1,
				@CurrentTime,
				@CurrentTime
			);

			SET @ExistingCartId = SCOPE_IDENTITY();

			-- Log the cart addition activity
			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				@UserId,
				'CART_ADD',
				'Added to cart: ' + @ProductName + ' (Quantity: ' + CAST(@Quantity AS VARCHAR(10)) + ')',
				@IpAddress,
				@UserAgent,
				@CurrentTime
			);

			-- Return new cart item info
			SELECT 
				@ExistingCartId AS CartId,
				@UserId AS UserId,
				@ProductId AS ProductId,
				@ProductName AS ProductName,
				@Quantity AS Quantity,
				@ProductPrice AS Price,
				(@Quantity * @ProductPrice) AS ItemTotal,
				'Product added to cart successfully' AS Message,
				@CurrentTime AS AddedDate;
		END

		-- Optional: Clean up old inactive cart items for this user (housekeeping)
		DELETE FROM CartItems 
		WHERE UserId = @UserId 
			AND Active = 0 
			AND UpdatedDate < DATEADD(DAY, -30, @CurrentTime);

		-- Get updated cart summary
		SELECT 
			COUNT(*) AS TotalUniqueItems,
			SUM(Quantity) AS TotalQuantity,
			SUM(ci.Quantity * p.Price) AS TotalAmount
		FROM CartItems ci
		INNER JOIN Products p ON ci.ProductId = p.ProductId
		WHERE ci.UserId = @UserId 
			AND ci.Active = 1
			AND p.Active = 1
			AND (@TenantId IS NULL OR ci.TenantId = @TenantId);

		COMMIT TRANSACTION;

	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		-- Log the failed cart operation
		IF @UserId IS NOT NULL
		BEGIN
			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				@UserId,
				'CART_ADD_FAILED',
				'Failed to add item to cart: ' + @ErrorMessage,
				@IpAddress,
				@UserAgent,
				GETUTCDATE()
			);
		END

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END


CREATE PROCEDURE [dbo].[SP_REMOVE_ITEM_FROM_CART]
	@UserId BIGINT,
	@ProductId BIGINT,
	@TenantId BIGINT = NULL,
	@RemoveCompletely BIT = 1,
	@IpAddress NVARCHAR(45) = NULL,
	@UserAgent NVARCHAR(500) = NULL
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		DECLARE @ExistingCartId BIGINT = NULL;
		DECLARE @CurrentQuantity INT = 0;
		DECLARE @ProductName NVARCHAR(255) = '';
		DECLARE @ProductPrice DECIMAL(18,2) = 0;
		DECLARE @CurrentTime DATETIME = GETUTCDATE();
		DECLARE @RemovedQuantity INT = 0;
		DECLARE @ItemTotal DECIMAL(18,2) = 0;

		-- Validate user exists and is active
		IF NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1)
		BEGIN
			RAISERROR('User not found or inactive.', 16, 1);
			RETURN;
		END

		-- Check if item exists in cart and get details
		SELECT 
			@ExistingCartId = ci.CartId,
			@CurrentQuantity = ci.Quantity,
			@ProductName = p.ProductName,
			@ProductPrice = p.Price
		FROM CartItems ci
		INNER JOIN Products p ON ci.ProductId = p.ProductId
		WHERE ci.UserId = @UserId 
			AND ci.ProductId = @ProductId 
			AND ci.Active = 1
			AND p.Active = 1
			AND (@TenantId IS NULL OR ci.TenantId = @TenantId);

		-- Check if cart item exists
		IF @ExistingCartId IS NULL
		BEGIN
			RAISERROR('Product not found in cart.', 16, 1);
			RETURN;
		END

		-- Calculate removed quantity and item total
		SET @RemovedQuantity = @CurrentQuantity;
		SET @ItemTotal = @RemovedQuantity * @ProductPrice;

		-- Remove the item from cart (mark as inactive or delete)
		IF @RemoveCompletely = 1
		BEGIN
			-- Permanently delete the cart item
			DELETE FROM CartItems 
			WHERE CartId = @ExistingCartId;
		END
		ELSE
		BEGIN
			-- Mark as inactive (soft delete)
			UPDATE CartItems
			SET 
				Active = 0,
				UpdatedDate = @CurrentTime
			WHERE CartId = @ExistingCartId;
		END

		-- Log the cart removal activity
		INSERT INTO UserActivityLog (
			UserId,
			ActivityType,
			ActivityDescription,
			IpAddress,
			UserAgent,
			CreatedAt
		) VALUES (
			@UserId,
			'CART_REMOVE',
			'Removed from cart: ' + @ProductName + ' (Quantity: ' + CAST(@RemovedQuantity AS VARCHAR(10)) + ')',
			@IpAddress,
			@UserAgent,
			@CurrentTime
		);

		-- Return removed item details
		SELECT 
			@ExistingCartId AS CartId,
			@UserId AS UserId,
			@ProductId AS ProductId,
			@ProductName AS ProductName,
			@RemovedQuantity AS RemovedQuantity,
			@ProductPrice AS Price,
			@ItemTotal AS ItemTotal,
			'Product removed from cart successfully' AS Message,
			@CurrentTime AS RemovedDate;

		-- Get updated cart summary
		SELECT 
			COUNT(*) AS TotalUniqueItems,
			ISNULL(SUM(ci.Quantity), 0) AS TotalQuantity,
			ISNULL(SUM(ci.Quantity * p.Price), 0) AS TotalAmount
		FROM CartItems ci
		INNER JOIN Products p ON ci.ProductId = p.ProductId
		WHERE ci.UserId = @UserId 
			AND ci.Active = 1
			AND p.Active = 1
			AND (@TenantId IS NULL OR ci.TenantId = @TenantId);

		-- Optional: Get recommended products to replace the removed item
		SELECT TOP 5
			p.ProductId,
			p.ProductName,
			p.Price,
			p.Rating,
			p.BestSeller,
			p.Offer,
			pi.Poster AS ImageUrl
		FROM Products p
		LEFT JOIN ProductImages pi ON p.ProductId = pi.ProductId AND pi.Main = 1 AND pi.Active = 1
		WHERE p.Active = 1
			AND p.ProductId != @ProductId  -- Exclude the removed product
			AND p.Category = (
				SELECT Category 
				FROM Products 
				WHERE ProductId = @ProductId
			)
			AND (@TenantId IS NULL OR p.TenantId = @TenantId)
		ORDER BY p.Rating DESC, p.UserBuyCount DESC;

		-- Optional: Clean up old inactive cart items for this user (housekeeping)
		DELETE FROM CartItems 
		WHERE UserId = @UserId 
			AND Active = 0 
			AND UpdatedDate < DATEADD(DAY, -30, @CurrentTime);

		COMMIT TRANSACTION;

	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		-- Log the failed cart removal
		IF @UserId IS NOT NULL
		BEGIN
			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				@UserId,
				'CART_REMOVE_FAILED',
				'Failed to remove item from cart: ' + @ErrorMessage,
				@IpAddress,
				@UserAgent,
				GETUTCDATE()
			);
		END

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END


CREATE PROCEDURE [dbo].[SP_CLEAR_CART]
	@UserId BIGINT,
	@TenantId BIGINT = NULL,
	@ClearCompletely BIT = 1,
	@IpAddress NVARCHAR(45) = NULL,
	@UserAgent NVARCHAR(500) = NULL
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		DECLARE @CartItemCount INT = 0;
		DECLARE @TotalQuantity INT = 0;
		DECLARE @TotalValue DECIMAL(18,2) = 0;
		DECLARE @CurrentTime DATETIME = GETUTCDATE();
		DECLARE @AffectedRows INT = 0;

		-- Validate user exists and is active
		IF NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1)
		BEGIN
			RAISERROR('User not found or inactive.', 16, 1);
			RETURN;
		END

		-- Get current cart statistics before clearing
		SELECT 
			@CartItemCount = COUNT(*),
			@TotalQuantity = ISNULL(SUM(ci.Quantity), 0),
			@TotalValue = ISNULL(SUM(ci.Quantity * p.Price), 0)
		FROM CartItems ci
		INNER JOIN Products p ON ci.ProductId = p.ProductId
		WHERE ci.UserId = @UserId 
			AND ci.Active = 1
			AND p.Active = 1
			AND (@TenantId IS NULL OR ci.TenantId = @TenantId);

		-- Check if cart has any items
		IF @CartItemCount = 0
		BEGIN
			RAISERROR('Cart is already empty.', 16, 1);
			RETURN;
		END

		-- Clear the cart based on strategy
		IF @ClearCompletely = 1
		BEGIN
			-- Permanently delete all cart items
			DELETE FROM CartItems 
			WHERE UserId = @UserId 
				AND Active = 1
				AND (@TenantId IS NULL OR TenantId = @TenantId);

			SET @AffectedRows = @@ROWCOUNT;
		END
		ELSE
		BEGIN
			-- Mark all cart items as inactive (soft delete)
			UPDATE CartItems
			SET 
				Active = 0,
				UpdatedDate = @CurrentTime
			WHERE UserId = @UserId 
				AND Active = 1
				AND (@TenantId IS NULL OR TenantId = @TenantId);

			SET @AffectedRows = @@ROWCOUNT;
		END

		-- Verify that items were actually cleared
		IF @AffectedRows = 0
		BEGIN
			RAISERROR('Failed to clear cart items.', 16, 1);
			RETURN;
		END

		-- Log the cart clearing activity
		INSERT INTO UserActivityLog (
			UserId,
			ActivityType,
			ActivityDescription,
			IpAddress,
			UserAgent,
			CreatedAt
		) VALUES (
			@UserId,
			'CART_CLEAR',
			'Cart cleared - ' + CAST(@CartItemCount AS VARCHAR(10)) + ' items removed (Total Value: $' + CAST(@TotalValue AS VARCHAR(20)) + ')',
			@IpAddress,
			@UserAgent,
			@CurrentTime
		);

		-- Return clearing operation details
		SELECT 
			@UserId AS UserId,
			@CartItemCount AS ClearedItemCount,
			@TotalQuantity AS ClearedQuantity,
			@TotalValue AS ClearedValue,
			'Cart cleared successfully' AS Message,
			@CurrentTime AS ClearedDate,
			@ClearCompletely AS WasHardDelete;

		-- Get updated cart summary (should be empty)
		SELECT 
			COUNT(*) AS TotalUniqueItems,
			ISNULL(SUM(ci.Quantity), 0) AS TotalQuantity,
			ISNULL(SUM(ci.Quantity * p.Price), 0) AS TotalAmount
		FROM CartItems ci
		INNER JOIN Products p ON ci.ProductId = p.ProductId
		WHERE ci.UserId = @UserId 
			AND ci.Active = 1
			AND p.Active = 1
			AND (@TenantId IS NULL OR ci.TenantId = @TenantId);

		-- Optional: Get popular products for cart rebuilding suggestions
		SELECT TOP 10
			p.ProductId,
			p.ProductName,
			p.Price,
			p.Rating,
			p.BestSeller,
			p.Trending,
			p.UserBuyCount,
			p.Offer,
			pi.Poster AS ImageUrl,
			cat.Category AS CategoryName
		FROM Products p
		LEFT JOIN ProductImages pi ON p.ProductId = pi.ProductId AND pi.Main = 1 AND pi.Active = 1
		LEFT JOIN ProductCategories cat ON p.Category = cat.CategoryId
		WHERE p.Active = 1
			AND p.InStock = 1
			AND (@TenantId IS NULL OR p.TenantId = @TenantId)
		ORDER BY 
			p.BestSeller DESC,
			p.Rating DESC, 
			p.UserBuyCount DESC,
			p.Trending DESC;

		-- Optional: Clean up old inactive cart items for this user (housekeeping)
		DELETE FROM CartItems 
		WHERE UserId = @UserId 
			AND Active = 0 
			AND UpdatedDate < DATEADD(DAY, -30, @CurrentTime);

		-- Optional: Record user behavior for analytics
		INSERT INTO UserBehaviorAnalytics (
			UserId,
			ActionType,
			ActionDetails,
			ItemCount,
			TotalValue,
			CreatedAt
		) VALUES (
			@UserId,
			'CART_CLEARED',
			'User cleared entire cart',
			@CartItemCount,
			@TotalValue,
			@CurrentTime
		);

		COMMIT TRANSACTION;

	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		-- Log the failed cart clearing
		IF @UserId IS NOT NULL
		BEGIN
			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				@UserId,
				'CART_CLEAR_FAILED',
				'Failed to clear cart: ' + @ErrorMessage,
				@IpAddress,
				@UserAgent,
				GETUTCDATE()
			);
		END

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END


CREATE PROCEDURE [dbo].[SP_CREATE_ORDER]
	@UserId BIGINT,
	@TenantId BIGINT = NULL,
	@OrderItems NVARCHAR(MAX), -- JSON string containing order items
	@ShippingAddress NVARCHAR(MAX), -- JSON string containing shipping address
	@BillingAddress NVARCHAR(MAX), -- JSON string containing billing address
	@PaymentMethod NVARCHAR(MAX), -- JSON string containing payment method
	@ShippingMethod NVARCHAR(MAX), -- JSON string containing shipping method
	@OrderTotals NVARCHAR(MAX), -- JSON string containing order totals
	@Notes NVARCHAR(1000) = NULL,
	@IpAddress NVARCHAR(45) = NULL,
	@UserAgent NVARCHAR(500) = NULL
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		DECLARE @OrderId BIGINT;
		DECLARE @OrderNumber NVARCHAR(50);
		DECLARE @CurrentTime DATETIME = GETUTCDATE();
		DECLARE @OrderStatus NVARCHAR(50) = 'Pending';
		DECLARE @PaymentStatus NVARCHAR(50) = 'Pending';
		DECLARE @TotalAmount DECIMAL(18,2) = 0;
		DECLARE @ItemCount INT = 0;

		-- Validate user exists and is active
		IF NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1)
		BEGIN
			RAISERROR('User not found or inactive.', 16, 1);
			RETURN;
		END

		-- Parse order totals to get total amount
		IF @OrderTotals IS NOT NULL
		BEGIN
			SELECT @TotalAmount = CAST(JSON_VALUE(@OrderTotals, '$.total') AS DECIMAL(18,2));
		END

		-- Generate unique order number
		SET @OrderNumber = 'ORD' + FORMAT(@CurrentTime, 'yyyyMMdd') + FORMAT(ABS(CHECKSUM(NEWID())) % 100000, '00000');

		-- Ensure order number is unique
		WHILE EXISTS (SELECT 1 FROM Orders WHERE OrderNumber = @OrderNumber)
		BEGIN
			SET @OrderNumber = 'ORD' + FORMAT(@CurrentTime, 'yyyyMMdd') + FORMAT(ABS(CHECKSUM(NEWID())) % 100000, '00000');
		END

		-- Insert main order record
		INSERT INTO Orders (
			UserId,
			TenantId,
			OrderNumber,
			OrderStatus,
			PaymentStatus,
			TotalAmount,
			Subtotal,
			ShippingAmount,
			TaxAmount,
			DiscountAmount,
			Notes,
			ShippingAddress,
			BillingAddress,
			PaymentMethod,
			ShippingMethod,
			CreatedAt,
			UpdatedAt,
			Active
		)
		SELECT 
			@UserId,
			@TenantId,
			@OrderNumber,
			@OrderStatus,
			@PaymentStatus,
			@TotalAmount,
			CAST(JSON_VALUE(@OrderTotals, '$.subtotal') AS DECIMAL(18,2)),
			CAST(JSON_VALUE(@OrderTotals, '$.shipping') AS DECIMAL(18,2)),
			CAST(JSON_VALUE(@OrderTotals, '$.tax') AS DECIMAL(18,2)),
			CAST(JSON_VALUE(@OrderTotals, '$.discount') AS DECIMAL(18,2)),
			@Notes,
			@ShippingAddress,
			@BillingAddress,
			@PaymentMethod,
			@ShippingMethod,
			@CurrentTime,
			@CurrentTime,
			1;

		SET @OrderId = SCOPE_IDENTITY();

		-- Insert order items from JSON
		INSERT INTO OrderItems (
			OrderId,
			ProductId,
			ProductName,
			ProductImage,
			Price,
			Quantity,
			Total,
			CreatedAt,
			Active
		)
		SELECT 
			@OrderId,
			CAST(JSON_VALUE(value, '$.productId') AS BIGINT),
			JSON_VALUE(value, '$.productName'),
			JSON_VALUE(value, '$.productImage'),
			CAST(JSON_VALUE(value, '$.price') AS DECIMAL(18,2)),
			CAST(JSON_VALUE(value, '$.quantity') AS INT),
			CAST(JSON_VALUE(value, '$.total') AS DECIMAL(18,2)),
			@CurrentTime,
			1
		FROM OPENJSON(@OrderItems);

		-- Get item count
		SELECT @ItemCount = COUNT(*) FROM OrderItems WHERE OrderId = @OrderId AND Active = 1;

		-- Validate that we have items
		IF @ItemCount = 0
		BEGIN
			RAISERROR('Order must contain at least one item.', 16, 1);
			RETURN;
		END

		-- Update product inventory (reduce stock)
		UPDATE p
		SET 
			p.Quantity = p.Quantity - oi.Quantity,
			p.UserBuyCount = p.UserBuyCount + oi.Quantity,
			p.Modified = @CurrentTime
		FROM Products p
		INNER JOIN OrderItems oi ON p.ProductId = oi.ProductId
		WHERE oi.OrderId = @OrderId 
			AND oi.Active = 1
			AND p.Active = 1;

		-- Check for insufficient stock
		IF EXISTS (
			SELECT 1 
			FROM Products p
			INNER JOIN OrderItems oi ON p.ProductId = oi.ProductId
			WHERE oi.OrderId = @OrderId 
				AND p.Quantity < 0
		)
		BEGIN
			RAISERROR('Insufficient stock for one or more products.', 16, 1);
			RETURN;
		END

		-- Clear user's cart items that were ordered
		UPDATE CartItems
		SET 
			Active = 0,
			UpdatedDate = @CurrentTime
		WHERE UserId = @UserId 
			AND ProductId IN (
				SELECT ProductId 
				FROM OrderItems 
				WHERE OrderId = @OrderId AND Active = 1
			)
			AND Active = 1
			AND (@TenantId IS NULL OR TenantId = @TenantId);

		-- Log the order creation activity
		INSERT INTO UserActivityLog (
			UserId,
			ActivityType,
			ActivityDescription,
			IpAddress,
			UserAgent,
			CreatedAt
		) VALUES (
			@UserId,
			'ORDER_CREATED',
			'Order created: ' + @OrderNumber + ' (Items: ' + CAST(@ItemCount AS VARCHAR(10)) + ', Total: $' + CAST(@TotalAmount AS VARCHAR(20)) + ')',
			@IpAddress,
			@UserAgent,
			@CurrentTime
		);

		-- Record order analytics
		INSERT INTO OrderAnalytics (
			OrderId,
			UserId,
			OrderNumber,
			TotalAmount,
			ItemCount,
			OrderDate,
			TenantId,
			CreatedAt
		) VALUES (
			@OrderId,
			@UserId,
			@OrderNumber,
			@TotalAmount,
			@ItemCount,
			@CurrentTime,
			@TenantId,
			@CurrentTime
		);

		-- Send order confirmation (placeholder for email/notification)
		INSERT INTO NotificationQueue (
			UserId,
			NotificationType,
			Subject,
			Message,
			OrderId,
			CreatedAt,
			Status
		) VALUES (
			@UserId,
			'ORDER_CONFIRMATION',
			'Order Confirmation - ' + @OrderNumber,
			'Your order has been successfully placed. Order Number: ' + @OrderNumber,
			@OrderId,
			@CurrentTime,
			'Pending'
		);

		-- Return order creation details
		SELECT 
			@OrderId AS OrderId,
			@OrderNumber AS OrderNumber,
			@UserId AS UserId,
			@ItemCount AS ItemCount,
			@TotalAmount AS TotalAmount,
			@OrderStatus AS OrderStatus,
			@PaymentStatus AS PaymentStatus,
			'Order created successfully' AS Message,
			@CurrentTime AS CreatedDate;

		-- Return order summary
		SELECT 
			o.OrderId,
			o.OrderNumber,
			o.TotalAmount,
			o.OrderStatus,
			o.PaymentStatus,
			o.CreatedAt,
			COUNT(oi.OrderItemId) AS TotalItems,
			SUM(oi.Quantity) AS TotalQuantity
		FROM Orders o
		LEFT JOIN OrderItems oi ON o.OrderId = oi.OrderId AND oi.Active = 1
		WHERE o.OrderId = @OrderId
		GROUP BY o.OrderId, o.OrderNumber, o.TotalAmount, o.OrderStatus, o.PaymentStatus, o.CreatedAt;

		-- Return order items
		SELECT 
			oi.OrderItemId,
			oi.ProductId,
			oi.ProductName,
			oi.ProductImage,
			oi.Price,
			oi.Quantity,
			oi.Total,
			p.ProductCode,
			p.Category
		FROM OrderItems oi
		LEFT JOIN Products p ON oi.ProductId = p.ProductId
		WHERE oi.OrderId = @OrderId AND oi.Active = 1
		ORDER BY oi.OrderItemId;

		COMMIT TRANSACTION;

	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		-- Log the failed order creation
		IF @UserId IS NOT NULL
		BEGIN
			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				@UserId,
				'ORDER_CREATE_FAILED',
				'Failed to create order: ' + @ErrorMessage,
				@IpAddress,
				@UserAgent,
				GETUTCDATE()
			);
		END

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END

CREATE PROCEDURE [dbo].[SP_GET_ORDERS] @UserId BIGINT, @TenantId BIGINT = NULL, @Page INT = 1, @Limit INT = 10, @Status NVARCHAR(50) = NULL, @Search NVARCHAR(100) = NULL AS BEGIN SET NOCOUNT ON;
BEGIN TRY DECLARE @Offset INT; DECLARE @TotalCount INT;
-- Calculate offset for pagination SET @Offset = (@Page - 1) * @Limit;
-- Validate user exists and is active IF NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1) BEGIN RAISERROR('User not found or inactive.', 16, 1); RETURN; END
-- Get total count for pagination SELECT @TotalCount = COUNT(*) FROM Orders o WHERE o.UserId = @UserId AND o.Active = 1 AND (@TenantId IS NULL OR o.TenantId = @TenantId) AND (@Status IS NULL OR o.OrderStatus = @Status) AND (@Search IS NULL OR o.OrderNumber LIKE '%' + @Search + '%');
-- Get orders with pagination SELECT o.OrderId, o.OrderNumber, o.OrderStatus, o.PaymentStatus, o.TotalAmount, o.Subtotal, o.ShippingAmount, o.TaxAmount, o.DiscountAmount, o.Notes, o.CreatedAt, o.UpdatedAt, -- Calculate item count and total quantity COUNT(oi.OrderItemId) AS TotalItems, ISNULL(SUM(oi.Quantity), 0) AS TotalQuantity, -- Pagination info @TotalCount AS TotalCount, @Page AS CurrentPage, @Limit AS PageSize, CEILING(CAST(@TotalCount AS FLOAT) / @Limit) AS TotalPages, CASE WHEN @Page < CEILING(CAST(@TotalCount AS FLOAT) / @Limit) THEN 1 ELSE 0 END AS HasNext, CASE WHEN @Page > 1 THEN 1 ELSE 0 END AS HasPrevious FROM Orders o LEFT JOIN OrderItems oi ON o.OrderId = oi.OrderId AND oi.Active = 1 WHERE o.UserId = @UserId AND o.Active = 1 AND (@TenantId IS NULL OR o.TenantId = @TenantId) AND (@Status IS NULL OR o.OrderStatus = @Status) AND (@Search IS NULL OR o.OrderNumber LIKE '%' + @Search + '%') GROUP BY o.OrderId, o.OrderNumber, o.OrderStatus, o.PaymentStatus, o.TotalAmount, o.Subtotal, o.ShippingAmount, o.TaxAmount, o.DiscountAmount, o.Notes, o.CreatedAt, o.UpdatedAt ORDER BY o.CreatedAt DESC OFFSET @Offset ROWS FETCH NEXT @Limit ROWS ONLY;
-- Get order items for each order in the result set SELECT oi.OrderId, oi.OrderItemId, oi.ProductId, oi.ProductName, oi.ProductImage, oi.Price, oi.Quantity, oi.Total, p.ProductCode, p.Category, p.Rating, p.Offer FROM OrderItems oi LEFT JOIN Products p ON oi.ProductId = p.ProductId WHERE oi.OrderId IN ( SELECT o.OrderId FROM Orders o WHERE o.UserId = @UserId AND o.Active = 1 AND (@TenantId IS NULL OR o.TenantId = @TenantId) AND (@Status IS NULL OR o.OrderStatus = @Status) AND (@Search IS NULL OR o.OrderNumber LIKE '%' + @Search + '%') ORDER BY o.CreatedAt DESC OFFSET @Offset ROWS FETCH NEXT @Limit ROWS ONLY ) AND oi.Active = 1 ORDER BY oi.OrderId, oi.OrderItemId;
END TRY BEGIN CATCH DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH END

CREATE PROCEDURE [dbo].[SP_GET_ORDER_BY_ID] @OrderId BIGINT, @UserId BIGINT, @TenantId BIGINT = NULL AS BEGIN SET NOCOUNT ON;
BEGIN TRY -- Validate user exists and is active IF NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1) BEGIN RAISERROR('User not found or inactive.', 16, 1); RETURN; END
-- Validate order exists and belongs to user IF NOT EXISTS ( SELECT 1 FROM Orders WHERE OrderId = @OrderId AND UserId = @UserId AND Active = 1 AND (@TenantId IS NULL OR TenantId = @TenantId) ) BEGIN RAISERROR('Order not found or does not belong to user.', 16, 1); RETURN; END
-- Get order details SELECT o.OrderId, o.OrderNumber, o.OrderStatus, o.PaymentStatus, o.TotalAmount, o.Subtotal, o.ShippingAmount, o.TaxAmount, o.DiscountAmount, o.Notes, o.ShippingAddress, o.BillingAddress, o.PaymentMethod, o.ShippingMethod, o.CreatedAt, o.UpdatedAt, -- User information u.FirstName + ' ' + u.LastName AS CustomerName, u.Email AS CustomerEmail, u.Phone AS CustomerPhone, -- Calculate totals COUNT(oi.OrderItemId) AS TotalItems, ISNULL(SUM(oi.Quantity), 0) AS TotalQuantity FROM Orders o LEFT JOIN Users u ON o.UserId = u.UserId LEFT JOIN OrderItems oi ON o.OrderId = oi.OrderId AND oi.Active = 1 WHERE o.OrderId = @OrderId AND o.UserId = @UserId AND o.Active = 1 AND (@TenantId IS NULL OR o.TenantId = @TenantId) GROUP BY o.OrderId, o.OrderNumber, o.OrderStatus, o.PaymentStatus, o.TotalAmount, o.Subtotal, o.ShippingAmount, o.TaxAmount, o.DiscountAmount, o.Notes, o.ShippingAddress, o.BillingAddress, o.PaymentMethod, o.ShippingMethod, o.CreatedAt, o.UpdatedAt, u.FirstName, u.LastName, u.Email, u.Phone;
-- Get order items SELECT oi.OrderItemId, oi.ProductId, oi.ProductName, oi.ProductImage, oi.Price, oi.Quantity, oi.Total, oi.CreatedAt, -- Product details p.ProductCode, p.ProductDescription, p.Category, p.Rating, p.Offer, p.InStock, p.BestSeller FROM OrderItems oi LEFT JOIN Products p ON oi.ProductId = p.ProductId WHERE oi.OrderId = @OrderId AND oi.Active = 1 ORDER BY oi.OrderItemId;
-- Get order status history (if available) SELECT osh.StatusHistoryId, osh.PreviousStatus, osh.NewStatus, osh.StatusNote, osh.ChangedBy, osh.ChangedAt, u.FirstName + ' ' + u.LastName AS ChangedByName FROM OrderStatusHistory osh LEFT JOIN Users u ON osh.ChangedBy = u.UserId WHERE osh.OrderId = @OrderId ORDER BY osh.ChangedAt DESC;
-- Get order tracking information (if available) SELECT ot.TrackingId, ot.TrackingNumber, ot.Carrier, ot.TrackingStatus, ot.EstimatedDelivery, ot.ActualDelivery, ot.TrackingUrl, ot.CreatedAt, ot.UpdatedAt FROM OrderTracking ot WHERE ot.OrderId = @OrderId AND ot.Active = 1 ORDER BY ot.CreatedAt DESC;
END TRY BEGIN CATCH DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH END


CREATE PROCEDURE [dbo].[SP_CANCEL_ORDER]
	@OrderId BIGINT,
	@UserId BIGINT,
	@TenantId BIGINT = NULL,
	@CancelReason NVARCHAR(500) = NULL,
	@CancelledBy BIGINT = NULL,
	@IpAddress NVARCHAR(45) = NULL,
	@UserAgent NVARCHAR(500) = NULL
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		DECLARE @CurrentStatus NVARCHAR(50);
		DECLARE @CurrentTime DATETIME = GETUTCDATE();
		DECLARE @OrderNumber NVARCHAR(50);
		DECLARE @TotalAmount DECIMAL(18,2);
		DECLARE @RefundAmount DECIMAL(18,2) = 0;
		DECLARE @CanCancel BIT = 0;

		-- Validate user exists and is active
		IF NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1)
		BEGIN
			RAISERROR('User not found or inactive.', 16, 1);
			RETURN;
		END

		-- Validate order exists and belongs to user
		SELECT 
			@CurrentStatus = OrderStatus,
			@OrderNumber = OrderNumber,
			@TotalAmount = TotalAmount
		FROM Orders 
		WHERE OrderId = @OrderId 
			AND UserId = @UserId 
			AND Active = 1
			AND (@TenantId IS NULL OR TenantId = @TenantId);

		IF @CurrentStatus IS NULL
		BEGIN
			RAISERROR('Order not found or does not belong to user.', 16, 1);
			RETURN;
		END

		-- Check if order can be cancelled based on current status
		IF @CurrentStatus IN ('Pending', 'Confirmed', 'Processing')
		BEGIN
			SET @CanCancel = 1;
			SET @RefundAmount = @TotalAmount; -- Full refund for early stage cancellations
		END
		ELSE IF @CurrentStatus = 'Shipped'
		BEGIN
			SET @CanCancel = 1;
			SET @RefundAmount = @TotalAmount * 0.95; -- 95% refund for shipped orders (5% handling fee)
		END
		ELSE IF @CurrentStatus IN ('Delivered', 'Cancelled', 'Refunded', 'Returned')
		BEGIN
			SET @CanCancel = 0;
		END

		IF @CanCancel = 0
		BEGIN
			RAISERROR('Order cannot be cancelled in current status: %s', 16, 1, @CurrentStatus);
			RETURN;
		END

		-- Update order status to cancelled
		UPDATE Orders
		SET 
			OrderStatus = 'Cancelled',
			PaymentStatus = CASE 
				WHEN PaymentStatus = 'Paid' THEN 'Refund_Pending'
				ELSE PaymentStatus
			END,
			UpdatedAt = @CurrentTime,
			CancelledAt = @CurrentTime,
			CancelReason = @CancelReason,
			CancelledBy = ISNULL(@CancelledBy, @UserId)
		WHERE OrderId = @OrderId;

		-- Add status history record
		INSERT INTO OrderStatusHistory (
			OrderId,
			PreviousStatus,
			NewStatus,
			StatusNote,
			ChangedBy,
			ChangedAt,
			CreatedAt
		) VALUES (
			@OrderId,
			@CurrentStatus,
			'Cancelled',
			ISNULL(@CancelReason, 'Order cancelled by customer'),
			ISNULL(@CancelledBy, @UserId),
			@CurrentTime,
			@CurrentTime
		);

		-- Restore product inventory
		UPDATE p
		SET 
			p.Quantity = p.Quantity + oi.Quantity,
			p.UserBuyCount = CASE 
				WHEN p.UserBuyCount >= oi.Quantity THEN p.UserBuyCount - oi.Quantity
				ELSE 0
			END,
			p.Modified = @CurrentTime
		FROM Products p
		INNER JOIN OrderItems oi ON p.ProductId = oi.ProductId
		WHERE oi.OrderId = @OrderId 
			AND oi.Active = 1
			AND p.Active = 1;

		-- Create refund record if payment was made
		IF EXISTS (SELECT 1 FROM Orders WHERE OrderId = @OrderId AND PaymentStatus IN ('Paid', 'Refund_Pending'))
		BEGIN
			INSERT INTO OrderRefunds (
				OrderId,
				RefundAmount,
				RefundReason,
				RefundStatus,
				RefundMethod,
				RequestedBy,
				RequestedAt,
				CreatedAt,
				Active
			) VALUES (
				@OrderId,
				@RefundAmount,
				ISNULL(@CancelReason, 'Order cancellation'),
				'Pending',
				'Original_Payment_Method',
				ISNULL(@CancelledBy, @UserId),
				@CurrentTime,
				@CurrentTime,
				1
			);
		END

		-- Log the cancellation activity
		INSERT INTO UserActivityLog (
			UserId,
			ActivityType,
			ActivityDescription,
			IpAddress,
			UserAgent,
			CreatedAt
		) VALUES (
			@UserId,
			'ORDER_CANCELLED',
			'Order cancelled: ' + @OrderNumber + ' (Reason: ' + ISNULL(@CancelReason, 'Customer request') + ')',
			@IpAddress,
			@UserAgent,
			@CurrentTime
		);

		-- Queue cancellation notification
		INSERT INTO NotificationQueue (
			UserId,
			NotificationType,
			Subject,
			Message,
			OrderId,
			CreatedAt,
			Status
		) VALUES (
			@UserId,
			'ORDER_CANCELLED',
			'Order Cancellation Confirmation - ' + @OrderNumber,
			'Your order has been successfully cancelled. ' + 
			CASE WHEN @RefundAmount > 0 THEN 'Refund of $' + CAST(@RefundAmount AS VARCHAR(20)) + ' will be processed.' ELSE '' END,
			@OrderId,
			@CurrentTime,
			'Pending'
		);

		-- Return cancellation details
		SELECT 
			@OrderId AS OrderId,
			@OrderNumber AS OrderNumber,
			@UserId AS UserId,
			@CurrentStatus AS PreviousStatus,
			'Cancelled' AS NewStatus,
			@RefundAmount AS RefundAmount,
			ISNULL(@CancelReason, 'Order cancelled by customer') AS CancelReason,
			'Order cancelled successfully' AS Message,
			@CurrentTime AS CancelledDate,
			CASE WHEN @RefundAmount > 0 THEN 1 ELSE 0 END AS RefundInitiated;

		COMMIT TRANSACTION;

	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		-- Log the failed cancellation
		IF @UserId IS NOT NULL
		BEGIN
			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				@UserId,
				'ORDER_CANCEL_FAILED',
				'Failed to cancel order: ' + @ErrorMessage,
				@IpAddress,
				@UserAgent,
				GETUTCDATE()
			);
		END

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END


CREATE PROCEDURE [dbo].[SP_UPDATE_ORDER_STATUS]
	@OrderId BIGINT,
	@UserId BIGINT,
	@TenantId BIGINT = NULL,
	@NewStatus NVARCHAR(50),
	@StatusNote NVARCHAR(1000) = NULL,
	@TrackingNumber NVARCHAR(100) = NULL,
	@Carrier NVARCHAR(100) = NULL,
	@EstimatedDelivery DATETIME = NULL,
	@UpdatedBy BIGINT = NULL,
	@IpAddress NVARCHAR(45) = NULL,
	@UserAgent NVARCHAR(500) = NULL
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		DECLARE @CurrentStatus NVARCHAR(50);
		DECLARE @CurrentTime DATETIME = GETUTCDATE();
		DECLARE @OrderNumber NVARCHAR(50);
		DECLARE @PaymentStatus NVARCHAR(50);
		DECLARE @IsValidTransition BIT = 0;
		DECLARE @TrackingId BIGINT;

		-- Validate user exists and is active (for customer updates)
		IF @UserId IS NOT NULL AND NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1)
		BEGIN
			RAISERROR('User not found or inactive.', 16, 1);
			RETURN;
		END

		-- Validate order exists
		SELECT 
			@CurrentStatus = OrderStatus,
			@OrderNumber = OrderNumber,
			@PaymentStatus = PaymentStatus
		FROM Orders 
		WHERE OrderId = @OrderId 
			AND (@UserId IS NULL OR UserId = @UserId)  -- Allow admin updates without user restriction
			AND Active = 1
			AND (@TenantId IS NULL OR TenantId = @TenantId);

		IF @CurrentStatus IS NULL
		BEGIN
			RAISERROR('Order not found or access denied.', 16, 1);
			RETURN;
		END

		-- Validate status transition
		IF (@CurrentStatus = 'Pending' AND @NewStatus IN ('Confirmed', 'Cancelled'))
			OR (@CurrentStatus = 'Confirmed' AND @NewStatus IN ('Processing', 'Cancelled'))
			OR (@CurrentStatus = 'Processing' AND @NewStatus IN ('Shipped', 'Cancelled'))
			OR (@CurrentStatus = 'Shipped' AND @NewStatus IN ('Delivered', 'Returned'))
			OR (@CurrentStatus = 'Delivered' AND @NewStatus = 'Returned')
			OR (@CurrentStatus = @NewStatus) -- Allow same status with note updates
		BEGIN
			SET @IsValidTransition = 1;
		END

		IF @IsValidTransition = 0
		BEGIN
			RAISERROR('Invalid status transition from %s to %s', 16, 1, @CurrentStatus, @NewStatus);
			RETURN;
		END

		-- Update order status
		UPDATE Orders
		SET 
			OrderStatus = @NewStatus,
			UpdatedAt = @CurrentTime,
			-- Update specific fields based on status
			ShippedAt = CASE WHEN @NewStatus = 'Shipped' THEN @CurrentTime ELSE ShippedAt END,
			DeliveredAt = CASE WHEN @NewStatus = 'Delivered' THEN @CurrentTime ELSE DeliveredAt END,
			PaymentStatus = CASE 
				WHEN @NewStatus = 'Delivered' AND @PaymentStatus = 'Pending' THEN 'Paid'
				ELSE @PaymentStatus
			END
		WHERE OrderId = @OrderId;

		-- Add status history record
		INSERT INTO OrderStatusHistory (
			OrderId,
			PreviousStatus,
			NewStatus,
			StatusNote,
			ChangedBy,
			ChangedAt,
			CreatedAt
		) VALUES (
			@OrderId,
			@CurrentStatus,
			@NewStatus,
			@StatusNote,
			ISNULL(@UpdatedBy, @UserId),
			@CurrentTime,
			@CurrentTime
		);

		-- Handle tracking information for shipped status
		IF @NewStatus = 'Shipped' AND @TrackingNumber IS NOT NULL
		BEGIN
			-- Check if tracking record exists
			SELECT @TrackingId = TrackingId 
			FROM OrderTracking 
			WHERE OrderId = @OrderId AND Active = 1;

			IF @TrackingId IS NOT NULL
			BEGIN
				-- Update existing tracking record
				UPDATE OrderTracking
				SET 
					TrackingNumber = @TrackingNumber,
					Carrier = ISNULL(@Carrier, Carrier),
					TrackingStatus = 'Shipped',
					EstimatedDelivery = ISNULL(@EstimatedDelivery, EstimatedDelivery),
					UpdatedAt = @CurrentTime
				WHERE TrackingId = @TrackingId;
			END
			ELSE
			BEGIN
				-- Create new tracking record
				INSERT INTO OrderTracking (
					OrderId,
					TrackingNumber,
					Carrier,
					TrackingStatus,
					EstimatedDelivery,
					TrackingUrl,
					CreatedAt,
					UpdatedAt,
					Active
				) VALUES (
					@OrderId,
					@TrackingNumber,
					@Carrier,
					'Shipped',
					@EstimatedDelivery,
					CASE 
						WHEN @Carrier = 'FedEx' THEN 'https://fedex.com/track/' + @TrackingNumber
						WHEN @Carrier = 'UPS' THEN 'https://ups.com/track/' + @TrackingNumber
						WHEN @Carrier = 'DHL' THEN 'https://dhl.com/track/' + @TrackingNumber
						ELSE NULL
					END,
					@CurrentTime,
					@CurrentTime,
					1
				);
			END
		END

		-- Update tracking status for delivered orders
		IF @NewStatus = 'Delivered'
		BEGIN
			UPDATE OrderTracking
			SET 
				TrackingStatus = 'Delivered',
				ActualDelivery = @CurrentTime,
				UpdatedAt = @CurrentTime
			WHERE OrderId = @OrderId AND Active = 1;
		END

		-- Log the status update activity
		INSERT INTO UserActivityLog (
			UserId,
			ActivityType,
			ActivityDescription,
			IpAddress,
			UserAgent,
			CreatedAt
		) VALUES (
			ISNULL(@UserId, @UpdatedBy),
			'ORDER_STATUS_UPDATED',
			'Order status updated: ' + @OrderNumber + ' (' + @CurrentStatus + '  ' + @NewStatus + ')',
			@IpAddress,
			@UserAgent,
			@CurrentTime
		);

		-- Queue status update notification
		INSERT INTO NotificationQueue (
			UserId,
			NotificationType,
			Subject,
			Message,
			OrderId,
			CreatedAt,
			Status
		) VALUES (
			@UserId,
			'ORDER_STATUS_UPDATE',
			'Order Status Update - ' + @OrderNumber,
			'Your order status has been updated to: ' + @NewStatus + 
			CASE 
				WHEN @TrackingNumber IS NOT NULL THEN '. Tracking Number: ' + @TrackingNumber
				ELSE ''
			END,
			@OrderId,
			@CurrentTime,
			'Pending'
		);

		-- Return status update details
		SELECT 
			@OrderId AS OrderId,
			@OrderNumber AS OrderNumber,
			ISNULL(@UserId, @UpdatedBy) AS UserId,
			@CurrentStatus AS PreviousStatus,
			@NewStatus AS NewStatus,
			@TrackingNumber AS TrackingNumber,
			@Carrier AS Carrier,
			@EstimatedDelivery AS EstimatedDelivery,
			ISNULL(@StatusNote, 'Order status updated') AS StatusNote,
			'Order status updated successfully' AS Message,
			@CurrentTime AS UpdatedDate;

		-- Return updated tracking info if available
		SELECT 
			t.TrackingId,
			t.TrackingNumber,
			t.Carrier,
			t.TrackingStatus,
			t.EstimatedDelivery,
			t.ActualDelivery,
			t.TrackingUrl,
			t.UpdatedAt
		FROM OrderTracking t
		WHERE t.OrderId = @OrderId AND t.Active = 1;

		COMMIT TRANSACTION;

	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		-- Log the failed status update
		IF @UserId IS NOT NULL OR @UpdatedBy IS NOT NULL
		BEGIN
			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				ISNULL(@UserId, @UpdatedBy),
				'ORDER_STATUS_UPDATE_FAILED',
				'Failed to update order status: ' + @ErrorMessage,
				@IpAddress,
				@UserAgent,
				GETUTCDATE()
			);
		END

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END

CREATE PROCEDURE [dbo].[SP_ADMIN_GET_ALL_USERS] @AdminUserId BIGINT, @TenantId BIGINT = NULL, @Page INT = 1, @Limit INT = 10, @Search NVARCHAR(100) = NULL, @Role NVARCHAR(50) = NULL, @Status NVARCHAR(50) = NULL AS BEGIN SET NOCOUNT ON;
BEGIN TRY DECLARE @Offset INT; DECLARE @TotalCount INT;
-- Calculate offset for pagination SET @Offset = (@Page - 1) * @Limit;
-- Validate admin user exists and has admin role IF NOT EXISTS ( SELECT 1 FROM Users u INNER JOIN UserRoles ur ON u.UserId = ur.UserId INNER JOIN Roles r ON ur.RoleId = r.RoleId WHERE u.UserId = @AdminUserId AND u.Active = 1 AND r.RoleName IN ('Admin', 'SuperAdmin') AND ur.Active = 1 ) BEGIN RAISERROR('User not found or insufficient privileges.', 16, 1); RETURN; END
-- Get total count for pagination SELECT @TotalCount = COUNT(DISTINCT u.UserId) FROM Users u LEFT JOIN UserRoles ur ON u.UserId = ur.UserId AND ur.Active = 1 LEFT JOIN Roles r ON ur.RoleId = r.RoleId AND r.Active = 1 WHERE u.Active = 1 AND (@TenantId IS NULL OR u.TenantId = @TenantId) AND (@Search IS NULL OR u.FirstName + ' ' + u.LastName LIKE '%' + @Search + '%' OR u.Email LIKE '%' + @Search + '%') AND (@Role IS NULL OR r.RoleName = @Role) AND (@Status IS NULL OR (@Status = 'active' AND u.Active = 1 AND u.AccountLocked = 0) OR (@Status = 'locked' AND u.AccountLocked = 1) OR (@Status = 'inactive' AND u.Active = 0) );
-- Get users with pagination SELECT u.UserId AS Id, u.FirstName + ' ' + u.LastName AS Name, u.Email, u.Phone, ISNULL(r.RoleName, 'customer') AS Role, CASE WHEN u.EmailVerified = 1 THEN 1 ELSE 0 END AS EmailVerified, CASE WHEN u.PhoneVerified = 1 THEN 1 ELSE 0 END AS PhoneVerified, CASE WHEN u.Active = 0 THEN 'inactive' WHEN u.AccountLocked = 1 THEN 'locked' ELSE 'active' END AS Status, u.CreatedAt, u.LastLogin, u.TenantId, u.DateOfBirth, u.Gender, u.Country, u.City, u.State, u.PostalCode, u.CompanyName, u.JobTitle, -- Calculate order statistics ISNULL(orderStats.OrderCount, 0) AS OrderCount, ISNULL(orderStats.TotalSpent, 0) AS TotalSpent, ISNULL(orderStats.LastOrderDate, NULL) AS LastOrderDate, -- Pagination info @TotalCount AS TotalCount, @Page AS CurrentPage, @Limit AS PageSize, CEILING(CAST(@TotalCount AS FLOAT) / @Limit) AS TotalPages, CASE WHEN @Page < CEILING(CAST(@TotalCount AS FLOAT) / @Limit) THEN 1 ELSE 0 END AS HasNext, CASE WHEN @Page > 1 THEN 1 ELSE 0 END AS HasPrevious FROM Users u LEFT JOIN UserRoles ur ON u.UserId = ur.UserId AND ur.Active = 1 LEFT JOIN Roles r ON ur.RoleId = r.RoleId AND r.Active = 1 LEFT JOIN ( SELECT o.UserId, COUNT(o.OrderId) AS OrderCount, SUM(o.TotalAmount) AS TotalSpent, MAX(o.CreatedAt) AS LastOrderDate FROM Orders o WHERE o.Active = 1 GROUP BY o.UserId ) orderStats ON u.UserId = orderStats.UserId WHERE u.Active = 1 AND (@TenantId IS NULL OR u.TenantId = @TenantId) AND (@Search IS NULL OR u.FirstName + ' ' + u.LastName LIKE '%' + @Search + '%' OR u.Email LIKE '%' + @Search + '%') AND (@Role IS NULL OR r.RoleName = @Role) AND (@Status IS NULL OR (@Status = 'active' AND u.Active = 1 AND u.AccountLocked = 0) OR (@Status = 'locked' AND u.AccountLocked = 1) OR (@Status = 'inactive' AND u.Active = 0) ) ORDER BY u.CreatedAt DESC OFFSET @Offset ROWS FETCH NEXT @Limit ROWS ONLY;
-- Get user permissions for each user in the result set SELECT u.UserId, p.PermissionName, p.PermissionDescription FROM Users u INNER JOIN UserRoles ur ON u.UserId = ur.UserId AND ur.Active = 1 INNER JOIN RolePermissions rp ON ur.RoleId = rp.RoleId AND rp.Active = 1 INNER JOIN Permissions p ON rp.PermissionId = p.PermissionId AND p.Active = 1 WHERE u.UserId IN ( SELECT u2.UserId FROM Users u2 LEFT JOIN UserRoles ur2 ON u2.UserId = ur2.UserId AND ur2.Active = 1 LEFT JOIN Roles r2 ON ur2.RoleId = r2.RoleId AND r2.Active = 1 WHERE u2.Active = 1 AND (@TenantId IS NULL OR u2.TenantId = @TenantId) AND (@Search IS NULL OR u2.FirstName + ' ' + u2.LastName LIKE '%' + @Search + '%' OR u2.Email LIKE '%' + @Search + '%') AND (@Role IS NULL OR r2.RoleName = @Role) AND (@Status IS NULL OR (@Status = 'active' AND u2.Active = 1 AND u2.AccountLocked = 0) OR (@Status = 'locked' AND u2.AccountLocked = 1) OR (@Status = 'inactive' AND u2.Active = 0) ) ORDER BY u2.CreatedAt DESC OFFSET @Offset ROWS FETCH NEXT @Limit ROWS ONLY ) ORDER BY u.UserId, p.PermissionName;
END TRY BEGIN CATCH DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH END


CREATE PROCEDURE [dbo].[SP_ADMIN_UPDATE_USER_ROLE]
	@AdminUserId BIGINT,
	@UserId BIGINT,
	@TenantId BIGINT = NULL,
	@RoleName NVARCHAR(50),
	@Permissions NVARCHAR(MAX) = NULL, -- JSON array of permission names
	@IpAddress NVARCHAR(45) = NULL,
	@UserAgent NVARCHAR(500) = NULL
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		DECLARE @CurrentTime DATETIME = GETUTCDATE();
		DECLARE @RoleId BIGINT;
		DECLARE @TargetUserName NVARCHAR(200);
		DECLARE @OldRoleName NVARCHAR(50);

		-- Validate admin user exists and has admin role
		IF NOT EXISTS (
			SELECT 1 FROM Users u
			INNER JOIN UserRoles ur ON u.UserId = ur.UserId
			INNER JOIN Roles r ON ur.RoleId = r.RoleId
			WHERE u.UserId = @AdminUserId 
				AND u.Active = 1 
				AND r.RoleName IN ('Admin', 'SuperAdmin')
				AND ur.Active = 1
		)
		BEGIN
			RAISERROR('Admin user not found or insufficient privileges.', 16, 1);
			RETURN;
		END

		-- Validate target user exists
		SELECT 
			@TargetUserName = u.FirstName + ' ' + u.LastName
		FROM Users u
		WHERE u.UserId = @UserId 
			AND u.Active = 1
			AND (@TenantId IS NULL OR u.TenantId = @TenantId);

		IF @TargetUserName IS NULL
		BEGIN
			RAISERROR('Target user not found or inactive.', 16, 1);
			RETURN;
		END

		-- Get current role
		SELECT @OldRoleName = r.RoleName
		FROM UserRoles ur
		INNER JOIN Roles r ON ur.RoleId = r.RoleId
		WHERE ur.UserId = @UserId AND ur.Active = 1;

		-- Validate new role exists
		SELECT @RoleId = RoleId
		FROM Roles
		WHERE RoleName = @RoleName AND Active = 1;

		IF @RoleId IS NULL
		BEGIN
			RAISERROR('Role not found: %s', 16, 1, @RoleName);
			RETURN;
		END

		-- Deactivate current user roles
		UPDATE UserRoles
		SET 
			Active = 0,
			UpdatedAt = @CurrentTime
		WHERE UserId = @UserId AND Active = 1;

		-- Assign new role
		INSERT INTO UserRoles (
			UserId,
			RoleId,
			AssignedBy,
			AssignedAt,
			CreatedAt,
			UpdatedAt,
			Active
		) VALUES (
			@UserId,
			@RoleId,
			@AdminUserId,
			@CurrentTime,
			@CurrentTime,
			@CurrentTime,
			1
		);

		-- Handle custom permissions if provided
		IF @Permissions IS NOT NULL AND @Permissions != ''
		BEGIN
			-- Remove existing custom permissions for this user
			DELETE FROM UserCustomPermissions 
			WHERE UserId = @UserId;

			-- Parse and insert new permissions
			DECLARE @PermissionName NVARCHAR(100);
			DECLARE @PermissionId BIGINT;

			-- Use OPENJSON to parse the permissions array
			DECLARE permission_cursor CURSOR FOR
			SELECT JSON_VALUE(value, '$') AS PermissionName
			FROM OPENJSON(@Permissions);

			OPEN permission_cursor;
			FETCH NEXT FROM permission_cursor INTO @PermissionName;

			WHILE @@FETCH_STATUS = 0
			BEGIN
				-- Get permission ID
				SELECT @PermissionId = PermissionId
				FROM Permissions
				WHERE PermissionName = @PermissionName AND Active = 1;

				-- Insert custom permission if valid
				IF @PermissionId IS NOT NULL
				BEGIN
					INSERT INTO UserCustomPermissions (
						UserId,
						PermissionId,
						GrantedBy,
						GrantedAt,
						CreatedAt,
						Active
					) VALUES (
						@UserId,
						@PermissionId,
						@AdminUserId,
						@CurrentTime,
						@CurrentTime,
						1
					);
				END

				FETCH NEXT FROM permission_cursor INTO @PermissionName;
			END

			CLOSE permission_cursor;
			DEALLOCATE permission_cursor;
		END

		-- Log the role change activity
		INSERT INTO UserActivityLog (
			UserId,
			ActivityType,
			ActivityDescription,
			IpAddress,
			UserAgent,
			CreatedAt,
			PerformedBy
		) VALUES (
			@UserId,
			'ROLE_UPDATED',
			'User role updated from ' + ISNULL(@OldRoleName, 'None') + ' to ' + @RoleName + ' by admin',
			@IpAddress,
			@UserAgent,
			@CurrentTime,
			@AdminUserId
		);

		-- Log admin activity
		INSERT INTO UserActivityLog (
			UserId,
			ActivityType,
			ActivityDescription,
			IpAddress,
			UserAgent,
			CreatedAt
		) VALUES (
			@AdminUserId,
			'ADMIN_ROLE_UPDATE',
			'Updated role for user ' + @TargetUserName + ' from ' + ISNULL(@OldRoleName, 'None') + ' to ' + @RoleName,
			@IpAddress,
			@UserAgent,
			@CurrentTime
		);

		-- Return updated user role information
		SELECT 
			@UserId AS UserId,
			@TargetUserName AS UserName,
			@OldRoleName AS PreviousRole,
			@RoleName AS NewRole,
			@AdminUserId AS UpdatedBy,
			@CurrentTime AS UpdatedAt,
			'User role updated successfully' AS Message;

		-- Return assigned permissions
		SELECT 
			p.PermissionName,
			p.PermissionDescription,
			CASE 
				WHEN ucp.PermissionId IS NOT NULL THEN 'Custom'
				ELSE 'Role-based'
			END AS PermissionSource
		FROM (
			-- Role-based permissions
			SELECT rp.PermissionId
			FROM UserRoles ur
			INNER JOIN RolePermissions rp ON ur.RoleId = rp.RoleId
			WHERE ur.UserId = @UserId AND ur.Active = 1 AND rp.Active = 1

			UNION

			-- Custom permissions
			SELECT ucp.PermissionId
			FROM UserCustomPermissions ucp
			WHERE ucp.UserId = @UserId AND ucp.Active = 1
		) combinedPermissions
		INNER JOIN Permissions p ON combinedPermissions.PermissionId = p.PermissionId
		LEFT JOIN UserCustomPermissions ucp ON p.PermissionId = ucp.PermissionId AND ucp.UserId = @UserId AND ucp.Active = 1
		WHERE p.Active = 1
		ORDER BY p.PermissionName;

		COMMIT TRANSACTION;

	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		-- Log the failed role update
		IF @AdminUserId IS NOT NULL
		BEGIN
			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				@AdminUserId,
				'ADMIN_ROLE_UPDATE_FAILED',
				'Failed to update user role: ' + @ErrorMessage,
				@IpAddress,
				@UserAgent,
				GETUTCDATE()
			);
		END

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END

CREATE PROCEDURE [dbo].[SP_ADMIN_GET_ALL_ORDERS] @AdminUserId BIGINT, @TenantId BIGINT = NULL, @Page INT = 1, @Limit INT = 10, @Status NVARCHAR(50) = NULL, @Search NVARCHAR(100) = NULL, @StartDate DATETIME = NULL, @EndDate DATETIME = NULL AS BEGIN SET NOCOUNT ON;
BEGIN TRY DECLARE @Offset INT; DECLARE @TotalCount INT;
-- Calculate offset for pagination SET @Offset = (@Page - 1) * @Limit;
-- Validate admin user exists and has admin role IF NOT EXISTS ( SELECT 1 FROM Users u INNER JOIN UserRoles ur ON u.UserId = ur.UserId INNER JOIN Roles r ON ur.RoleId = r.RoleId WHERE u.UserId = @AdminUserId AND u.Active = 1 AND r.RoleName IN ('Admin', 'SuperAdmin') AND ur.Active = 1 ) BEGIN RAISERROR('User not found or insufficient privileges.', 16, 1); RETURN; END
-- Set default date range if not provided IF @StartDate IS NULL SET @StartDate = DATEADD(MONTH, -6, GETUTCDATE()); -- Last 6 months
IF @EndDate IS NULL SET @EndDate = GETUTCDATE();
-- Get total count for pagination SELECT @TotalCount = COUNT(*) FROM Orders o INNER JOIN Users u ON o.UserId = u.UserId WHERE o.Active = 1 AND (@TenantId IS NULL OR o.TenantId = @TenantId) AND (@Status IS NULL OR o.OrderStatus = @Status) AND (@Search IS NULL OR o.OrderNumber LIKE '%' + @Search + '%' OR u.FirstName + ' ' + u.LastName LIKE '%' + @Search + '%' OR u.Email LIKE '%' + @Search + '%' ) AND o.CreatedAt >= @StartDate AND o.CreatedAt <= @EndDate;
-- Get orders with pagination SELECT o.OrderId, o.OrderNumber, u.FirstName + ' ' + u.LastName AS CustomerName, u.Email AS CustomerEmail, u.Phone AS CustomerPhone, o.OrderStatus AS Status, o.PaymentStatus, o.TotalAmount AS Total, o.Subtotal, o.ShippingAmount, o.TaxAmount, o.DiscountAmount, -- Calculate item statistics ISNULL(orderItems.ItemCount, 0) AS ItemCount, ISNULL(orderItems.TotalQuantity, 0) AS TotalQuantity, o.CreatedAt, o.UpdatedAt, o.ShippedAt, o.DeliveredAt, o.ShippingAddress, o.BillingAddress, o.PaymentMethod, o.ShippingMethod, o.Notes, -- Estimated delivery calculation CASE WHEN o.OrderStatus = 'Delivered' THEN o.DeliveredAt WHEN o.OrderStatus = 'Shipped' THEN DATEADD(DAY, 3, ISNULL(o.ShippedAt, o.UpdatedAt)) WHEN o.OrderStatus IN ('Processing', 'Confirmed') THEN DATEADD(DAY, 5, o.CreatedAt) ELSE DATEADD(DAY, 7, o.CreatedAt) END AS EstimatedDelivery, -- Customer information u.UserId AS CustomerId, u.TenantId AS CustomerTenantId, -- Pagination info @TotalCount AS TotalCount, @Page AS CurrentPage, @Limit AS PageSize, CEILING(CAST(@TotalCount AS FLOAT) / @Limit) AS TotalPages, CASE WHEN @Page < CEILING(CAST(@TotalCount AS FLOAT) / @Limit) THEN 1 ELSE 0 END AS HasNext, CASE WHEN @Page > 1 THEN 1 ELSE 0 END AS HasPrevious FROM Orders o INNER JOIN Users u ON o.UserId = u.UserId LEFT JOIN ( SELECT oi.OrderId, COUNT(oi.OrderItemId) AS ItemCount, SUM(oi.Quantity) AS TotalQuantity FROM OrderItems oi WHERE oi.Active = 1 GROUP BY oi.OrderId ) orderItems ON o.OrderId = orderItems.OrderId WHERE o.Active = 1 AND (@TenantId IS NULL OR o.TenantId = @TenantId) AND (@Status IS NULL OR o.OrderStatus = @Status) AND (@Search IS NULL OR o.OrderNumber LIKE '%' + @Search + '%' OR u.FirstName + ' ' + u.LastName LIKE '%' + @Search + '%' OR u.Email LIKE '%' + @Search + '%' ) AND o.CreatedAt >= @StartDate AND o.CreatedAt <= @EndDate ORDER BY o.CreatedAt DESC OFFSET @Offset ROWS FETCH NEXT @Limit ROWS ONLY;
-- Get order items for each order in the result set SELECT oi.OrderId, oi.OrderItemId, oi.ProductId, oi.ProductName, oi.ProductImage, oi.Price, oi.Quantity, oi.Total, p.ProductCode, p.Category, p.Rating, p.Offer FROM OrderItems oi LEFT JOIN Products p ON oi.ProductId = p.ProductId WHERE oi.OrderId IN ( SELECT o.OrderId FROM Orders o INNER JOIN Users u ON o.UserId = u.UserId WHERE o.Active = 1 AND (@TenantId IS NULL OR o.TenantId = @TenantId) AND (@Status IS NULL OR o.OrderStatus = @Status) AND (@Search IS NULL OR o.OrderNumber LIKE '%' + @Search + '%' OR u.FirstName + ' ' + u.LastName LIKE '%' + @Search + '%' OR u.Email LIKE '%' + @Search + '%' ) AND o.CreatedAt >= @StartDate AND o.CreatedAt <= @EndDate ORDER BY o.CreatedAt DESC OFFSET @Offset ROWS FETCH NEXT @Limit ROWS ONLY ) AND oi.Active = 1 ORDER BY oi.OrderId, oi.OrderItemId;
-- Get order statistics summary SELECT COUNT(*) AS TotalOrders, SUM(o.TotalAmount) AS TotalRevenue, AVG(o.TotalAmount) AS AverageOrderValue, COUNT(DISTINCT o.UserId) AS UniqueCustomers, SUM(CASE WHEN o.OrderStatus = 'Pending' THEN 1 ELSE 0 END) AS PendingOrders, SUM(CASE WHEN o.OrderStatus = 'Processing' THEN 1 ELSE 0 END) AS ProcessingOrders, SUM(CASE WHEN o.OrderStatus = 'Shipped' THEN 1 ELSE 0 END) AS ShippedOrders, SUM(CASE WHEN o.OrderStatus = 'Delivered' THEN 1 ELSE 0 END) AS DeliveredOrders, SUM(CASE WHEN o.OrderStatus = 'Cancelled' THEN 1 ELSE 0 END) AS CancelledOrders FROM Orders o WHERE o.Active = 1 AND (@TenantId IS NULL OR o.TenantId = @TenantId) AND o.CreatedAt >= @StartDate AND o.CreatedAt <= @EndDate;
END TRY BEGIN CATCH DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH END

CREATE PROCEDURE [dbo].[SP_ADD_PRODUCT_IMAGES] @ProductId BIGINT, @UserId BIGINT = NULL, @TenantId BIGINT = NULL, @Images NVARCHAR(MAX), -- JSON array of image data @IpAddress NVARCHAR(45) = NULL, @UserAgent NVARCHAR(500) = NULL AS BEGIN SET NOCOUNT ON;
BEGIN TRY BEGIN TRANSACTION;
DECLARE @CurrentTime DATETIME = GETUTCDATE(); DECLARE @ImageCount INT = 0; DECLARE @BaseUrl NVARCHAR(500) = 'https://cdn.example.com/images/';
-- Validate product exists and is active IF NOT EXISTS (SELECT 1 FROM Products WHERE ProductId = @ProductId AND Active = 1) BEGIN RAISERROR('Product not found or inactive.', 16, 1); RETURN; END
-- Validate user if provided IF @UserId IS NOT NULL AND NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1) BEGIN RAISERROR('User not found or inactive.', 16, 1); RETURN; END
-- Create temporary table to store image data CREATE TABLE #TempImages ( ImageName NVARCHAR(255), ContentType NVARCHAR(100), FileSize BIGINT, ImageData VARBINARY(MAX), ThumbnailData VARBINARY(MAX), IsMain BIT, OrderBy INT, ImageUrl NVARCHAR(500) );
-- Parse JSON and insert into temporary table INSERT INTO #TempImages (ImageName, ContentType, FileSize, ImageData, ThumbnailData, IsMain, OrderBy, ImageUrl) SELECT JSON_VALUE(value, '$.imageName') AS ImageName, JSON_VALUE(value, '$.contentType') AS ContentType, CAST(JSON_VALUE(value, '$.fileSize') AS BIGINT) AS FileSize, CAST(JSON_VALUE(value, '$.imageData') AS VARBINARY(MAX)) AS ImageData, CAST(JSON_VALUE(value, '$.thumbnailData') AS VARBINARY(MAX)) AS ThumbnailData, CAST(JSON_VALUE(value, '$.isMain') AS BIT) AS IsMain, CAST(JSON_VALUE(value, '$.orderBy') AS INT) AS OrderBy, @BaseUrl + 'product_' + CAST(@ProductId AS VARCHAR(20)) + '_' + JSON_VALUE(value, '$.imageName') AS ImageUrl FROM OPENJSON(@Images);
-- Count images to be added SELECT @ImageCount = COUNT(*) FROM #TempImages;
-- Validate at least one image IF @ImageCount = 0 BEGIN RAISERROR('At least one image is required.', 16, 1); RETURN; END
-- If main image is specified, unset other main images for this product IF EXISTS (SELECT 1 FROM #TempImages WHERE IsMain = 1) BEGIN UPDATE ProductImages SET Main = 0, Modified = @CurrentTime WHERE ProductId = @ProductId AND Active = 1; END
-- Insert new images INSERT INTO ProductImages ( ProductId, ImageName, ContentType, FileSize, ImageData, ThumbnailData, Poster, Main, Active, OrderBy, CreatedAt, Modified, CreatedBy ) SELECT @ProductId, ti.ImageName, ti.ContentType, ti.FileSize, ti.ImageData, ti.ThumbnailData, ti.ImageUrl, ti.IsMain, 1, -- Active ti.OrderBy, @CurrentTime, @CurrentTime, @UserId FROM #TempImages ti;
-- Update product modified date UPDATE Products SET Modified = @CurrentTime WHERE ProductId = @ProductId;
-- Log the image addition activity IF @UserId IS NOT NULL BEGIN INSERT INTO UserActivityLog ( UserId, ActivityType, ActivityDescription, IpAddress, UserAgent, CreatedAt ) VALUES ( @UserId, 'PRODUCT_IMAGES_ADDED', 'Added ' + CAST(@ImageCount AS VARCHAR(10)) + ' images to product ID: ' + CAST(@ProductId AS VARCHAR(20)), @IpAddress, @UserAgent, @CurrentTime ); END
-- Return inserted images SELECT pi.ImageId, pi.ProductId, pi.Poster, pi.Main, pi.Active, pi.OrderBy, pi.CreatedAt AS Created, pi.Modified, pi.ImageName, pi.ContentType, pi.FileSize FROM ProductImages pi WHERE pi.ProductId = @ProductId AND pi.CreatedAt = @CurrentTime AND pi.Active = 1 ORDER BY pi.OrderBy, pi.ImageId;
-- Clean up temporary table DROP TABLE #TempImages;
COMMIT TRANSACTION;
END TRY BEGIN CATCH IF @@TRANCOUNT > 0 ROLLBACK TRANSACTION;
-- Clean up temporary table if exists IF OBJECT_ID('tempdb..#TempImages') IS NOT NULL DROP TABLE #TempImages;
DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
-- Log the failed image addition IF @UserId IS NOT NULL BEGIN INSERT INTO UserActivityLog ( UserId, ActivityType, ActivityDescription, IpAddress, UserAgent, CreatedAt ) VALUES ( @UserId, 'PRODUCT_IMAGES_ADD_FAILED', 'Failed to add images to product ID: ' + CAST(@ProductId AS VARCHAR(20)) + ' - ' + @ErrorMessage, @IpAddress, @UserAgent, GETUTCDATE() ); END
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH END


CREATE PROCEDURE [dbo].[SP_UPDATE_PRODUCT_IMAGE]
	@ProductId BIGINT,
	@ImageId BIGINT,
	@UserId BIGINT = NULL,
	@TenantId BIGINT = NULL,
	@IsMain BIT = NULL,
	@Active BIT = NULL,
	@OrderBy INT = NULL,
	@IpAddress NVARCHAR(45) = NULL,
	@UserAgent NVARCHAR(500) = NULL
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		DECLARE @CurrentTime DATETIME = GETUTCDATE();
		DECLARE @OldMain BIT;
		DECLARE @OldActive BIT;
		DECLARE @OldOrderBy INT;
		DECLARE @ImageName NVARCHAR(255);

		-- Validate product exists and is active
		IF NOT EXISTS (SELECT 1 FROM Products WHERE ProductId = @ProductId AND Active = 1)
		BEGIN
			RAISERROR('Product not found or inactive.', 16, 1);
			RETURN;
		END

		-- Validate image exists and belongs to product
		SELECT 
			@OldMain = Main,
			@OldActive = Active,
			@OldOrderBy = OrderBy,
			@ImageName = ImageName
		FROM ProductImages 
		WHERE ImageId = @ImageId AND ProductId = @ProductId;

		IF @ImageName IS NULL
		BEGIN
			RAISERROR('Image not found or does not belong to this product.', 16, 1);
			RETURN;
		END

		-- Validate user if provided
		IF @UserId IS NOT NULL AND NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1)
		BEGIN
			RAISERROR('User not found or inactive.', 16, 1);
			RETURN;
		END

		-- If setting as main image, unset other main images for this product
		IF @IsMain = 1 AND @OldMain = 0
		BEGIN
			UPDATE ProductImages
			SET 
				Main = 0,
				Modified = @CurrentTime
			WHERE ProductId = @ProductId AND ImageId != @ImageId AND Active = 1;
		END

		-- Update the image
		UPDATE ProductImages
		SET 
			Main = ISNULL(@IsMain, Main),
			Active = ISNULL(@Active, Active),
			OrderBy = ISNULL(@OrderBy, OrderBy),
			Modified = @CurrentTime,
			ModifiedBy = @UserId
		WHERE ImageId = @ImageId AND ProductId = @ProductId;

		-- Update product modified date
		UPDATE Products
		SET Modified = @CurrentTime
		WHERE ProductId = @ProductId;

		-- Log the image update activity
		IF @UserId IS NOT NULL
		BEGIN
			DECLARE @Changes NVARCHAR(1000) = '';

			IF @IsMain IS NOT NULL AND @IsMain != @OldMain
				SET @Changes = @Changes + 'Main: ' + CAST(@OldMain AS VARCHAR(5)) + '  ' + CAST(@IsMain AS VARCHAR(5)) + '; ';

			IF @Active IS NOT NULL AND @Active != @OldActive
				SET @Changes = @Changes + 'Active: ' + CAST(@OldActive AS VARCHAR(5)) + '  ' + CAST(@Active AS VARCHAR(5)) + '; ';

			IF @OrderBy IS NOT NULL AND @OrderBy != @OldOrderBy
				SET @Changes = @Changes + 'OrderBy: ' + CAST(@OldOrderBy AS VARCHAR(10)) + '  ' + CAST(@OrderBy AS VARCHAR(10)) + '; ';

			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				@UserId,
				'PRODUCT_IMAGE_UPDATED',
				'Updated image ' + @ImageName + ' for product ID: ' + CAST(@ProductId AS VARCHAR(20)) + 
				CASE WHEN LEN(@Changes) > 0 THEN ' - Changes: ' + @Changes ELSE '' END,
				@IpAddress,
				@UserAgent,
				@CurrentTime
			);
		END

		-- Return updated image
		SELECT 
			pi.ImageId,
			pi.ProductId,
			pi.Poster,
			pi.Main,
			pi.Active,
			pi.OrderBy,
			pi.CreatedAt AS Created,
			pi.Modified,
			pi.ImageName,
			pi.ContentType,
			pi.FileSize,
			'Image updated successfully' AS Message
		FROM ProductImages pi
		WHERE pi.ImageId = @ImageId AND pi.ProductId = @ProductId;

		COMMIT TRANSACTION;

	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		-- Log the failed image update
		IF @UserId IS NOT NULL
		BEGIN
			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				@UserId,
				'PRODUCT_IMAGE_UPDATE_FAILED',
				'Failed to update image ID: ' + CAST(@ImageId AS VARCHAR(20)) + ' for product ID: ' + CAST(@ProductId AS VARCHAR(20)) + ' - ' + @ErrorMessage,
				@IpAddress,
				@UserAgent,
				GETUTCDATE()
			);
		END

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END


CREATE PROCEDURE [dbo].[SP_DELETE_PRODUCT_IMAGE]
	@ProductId BIGINT,
	@ImageId BIGINT,
	@UserId BIGINT = NULL,
	@TenantId BIGINT = NULL,
	@HardDelete BIT = 0,
	@IpAddress NVARCHAR(45) = NULL,
	@UserAgent NVARCHAR(500) = NULL
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
		BEGIN TRANSACTION;

		DECLARE @CurrentTime DATETIME = GETUTCDATE();
		DECLARE @ImageName NVARCHAR(255);
		DECLARE @IsMain BIT;
		DECLARE @IsActive BIT;

		-- Validate product exists and is active
		IF NOT EXISTS (SELECT 1 FROM Products WHERE ProductId = @ProductId AND Active = 1)
		BEGIN
			RAISERROR('Product not found or inactive.', 16, 1);
			RETURN;
		END

		-- Validate image exists and belongs to product
		SELECT 
			@ImageName = ImageName,
			@IsMain = Main,
			@IsActive = Active
		FROM ProductImages 
		WHERE ImageId = @ImageId AND ProductId = @ProductId;

		IF @ImageName IS NULL
		BEGIN
			RAISERROR('Image not found or does not belong to this product.', 16, 1);
			RETURN;
		END

		-- Check if image is already deleted (soft delete)
		IF @IsActive = 0 AND @HardDelete = 0
		BEGIN
			RAISERROR('Image is already deleted.', 16, 1);
			RETURN;
		END

		-- Validate user if provided
		IF @UserId IS NOT NULL AND NOT EXISTS (SELECT 1 FROM Users WHERE UserId = @UserId AND Active = 1)
		BEGIN
			RAISERROR('User not found or inactive.', 16, 1);
			RETURN;
		END

		-- Check if this is the last active image for the product
		DECLARE @ActiveImageCount INT;
		SELECT @ActiveImageCount = COUNT(*)
		FROM ProductImages 
		WHERE ProductId = @ProductId AND Active = 1 AND ImageId != @ImageId;

		-- Prevent deletion of the last image if it's the main image
		IF @ActiveImageCount = 0 AND @IsMain = 1
		BEGIN
			RAISERROR('Cannot delete the last active main image. Please add another image first.', 16, 1);
			RETURN;
		END

		-- Perform deletion
		IF @HardDelete = 1
		BEGIN
			-- Hard delete: Remove from database completely
			DELETE FROM ProductImages
			WHERE ImageId = @ImageId AND ProductId = @ProductId;
		END
		ELSE
		BEGIN
			-- Soft delete: Mark as inactive
			UPDATE ProductImages
			SET 
				Active = 0,
				Main = 0, -- Remove main status if it was main
				Modified = @CurrentTime,
				ModifiedBy = @UserId,
				DeletedAt = @CurrentTime,
				DeletedBy = @UserId
			WHERE ImageId = @ImageId AND ProductId = @ProductId;
		END

		-- If deleted image was main, set another active image as main
		IF @IsMain = 1 AND @ActiveImageCount > 0
		BEGIN
			UPDATE ProductImages
			SET 
				Main = 1,
				Modified = @CurrentTime
			WHERE ProductId = @ProductId 
				AND Active = 1 
				AND ImageId = (
					SELECT TOP 1 ImageId 
					FROM ProductImages 
					WHERE ProductId = @ProductId AND Active = 1 
					ORDER BY OrderBy, ImageId
				);
		END

		-- Update product modified date
		UPDATE Products
		SET Modified = @CurrentTime
		WHERE ProductId = @ProductId;

		-- Log the image deletion activity
		IF @UserId IS NOT NULL
		BEGIN
			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				@UserId,
				CASE WHEN @HardDelete = 1 THEN 'PRODUCT_IMAGE_HARD_DELETED' ELSE 'PRODUCT_IMAGE_SOFT_DELETED' END,
				CASE WHEN @HardDelete = 1 THEN 'Permanently deleted' ELSE 'Soft deleted' END + 
				' image ' + @ImageName + ' from product ID: ' + CAST(@ProductId AS VARCHAR(20)),
				@IpAddress,
				@UserAgent,
				@CurrentTime
			);
		END

		-- Return deletion confirmation
		SELECT 
			@ImageId AS ImageId,
			@ProductId AS ProductId,
			@ImageName AS ImageName,
			@IsMain AS WasMain,
			@HardDelete AS HardDeleted,
			@CurrentTime AS DeletedAt,
			@UserId AS DeletedBy,
			CASE WHEN @HardDelete = 1 THEN 'Image permanently deleted' ELSE 'Image soft deleted' END AS Message,
			@ActiveImageCount AS RemainingActiveImages;

		-- Return current active images for the product
		SELECT 
			pi.ImageId,
			pi.ProductId,
			pi.Poster,
			pi.Main,
			pi.Active,
			pi.OrderBy,
			pi.CreatedAt AS Created,
			pi.Modified
		FROM ProductImages pi
		WHERE pi.ProductId = @ProductId AND pi.Active = 1
		ORDER BY pi.OrderBy, pi.ImageId;

		COMMIT TRANSACTION;

	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION;

		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		-- Log the failed image deletion
		IF @UserId IS NOT NULL
		BEGIN
			INSERT INTO UserActivityLog (
				UserId,
				ActivityType,
				ActivityDescription,
				IpAddress,
				UserAgent,
				CreatedAt
			) VALUES (
				@UserId,
				'PRODUCT_IMAGE_DELETE_FAILED',
				'Failed to delete image ID: ' + CAST(@ImageId AS VARCHAR(20)) + ' from product ID: ' + CAST(@ProductId AS VARCHAR(20)) + ' - ' + @ErrorMessage,
				@IpAddress,
				@UserAgent,
				GETUTCDATE()
			);
		END

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END
